INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987', 2, 'Nyani walioenda huku na huko', 'assets/topic/asb/1160.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_3', 3, NULL, 'assets/topic/asb/1161.png', 'Watu wa kijiji cha Udongo walikumbwa na matatizo mengi. Walihuzunika kwa sababu wanyama pori waliyahatarisha maisha yao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_4', 3, NULL, 'assets/topic/asb/1162.png', 'Wanyama hao waliila mimea yao yote na kuwaiba watoto. Mahali hapo hapakuwa pazuri pa kuishi tena. Lakini, wangehamia wapi?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_5', 3, NULL, 'assets/topic/asb/1163.png', 'Watu wa familia moja walihamia milimani. Waliamini kuwa wangepata chakula huko.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_6', 3, NULL, 'assets/topic/asb/1164.png', 'Milimani, kulikuwa na vichaka, mapango na miti mingi. Walikula sungura na ndege.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_7', 3, NULL, 'assets/topic/asb/1165.png', 'Familia zingine ziliona jinsi familia ya milimani ilivyoishi vizuri. Mtu mmoja aliwashawishi, "Maisha ya milimani ni mazuri. Nanyi hamieni huku."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_8', 3, NULL, 'assets/topic/asb/1166.png', 'Familia zingine zilihamia milimani. Ziliishi mapangoni kulikokuwa na joto na usalama.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_9', 3, NULL, 'assets/topic/asb/1160.png', 'Watoto wa familia za milimani walizoe kupanda miti kutafuta matunda. Walining''inia kwenye matawi kama vile kima hufanya.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_10', 3, NULL, 'assets/topic/asb/1167.png', 'Kadri muda ulivyopita, ndivyo familia za milimani zilivyobadilika kimaumbile. Watoto wao walipunguza maongezi yao wakawa wanakoroma zaidi. Mapua ya watu wazima yalianza kuwa makubwa na waliota nywele zaidi kwenye miili yao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_11', 3, NULL, 'assets/topic/asb/1169.png', 'Kila walipotazamana, waliona kwamba meno yao yaliendelea kuwa marefu. Walianza kutembea kwa miguu minne.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_12', 3, NULL, 'assets/topic/asb/1175.png', 'Waligeuka wakawa viumbe wapya wasiokuwa wameonekana pahali pale mbeleni. Wakaitwa nyani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_13', 3, NULL, 'assets/topic/asb/1168.png', 'Mwanzoni, nyani hao waliishi kwa furaha. Badala ya kula sungura, walianza kuyala mabuu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_14', 3, NULL, NULL, 'Walisahau jinsi ya kutembea wima kama ilivyokuwa awali. Wakavua nguo zao kwa sababu miili yao sasa ilikuwa imefunikwa kwa manyoya meusi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_15', 3, NULL, 'assets/topic/asb/1170.png', 'Hata hivyo, walikumbuka kwamba walikuwa watu mbeleni. Walipoyaona mapua yao makubwa,  walichekana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_16', 3, NULL, 'assets/topic/asb/1171.png', 'Baadaye, walianza kukasirishwa na tabia zao wenyewe. Walipochekana, waliruka juu na chini kwa hasira. Vita vilizuka baina ya nyani hao wakalazimika kutawanyika.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2987_17', 3, NULL, 'assets/topic/asb/1173.png', 'Hiyo ndiyo sababu hata leo, nyani huishi katika makundi madogo na wala hawaishi kama taifa la nyani.', NULL);
