INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692', 2, 'Nyumba ya Kobe', 'assets/topic/asb/9213.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_3', 3, NULL, 'assets/topic/asb/9214.png', 'Siku moja, Kobe alikuwa akitembea nyasini. Alichunguza mbele na kukodolea nyasi macho akamwona Konokono.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_4', 3, NULL, NULL, '"Unatafuta nini?" Konokono alimwuliza. "Ninaitafuta nyumba yangu. Umeiona?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_5', 3, NULL, 'assets/topic/asb/9215.png', 'Alijibu, "La, sijaiona, lakini nitakusaidia kuitafuta!" Konokono alipanda juu ya gamba la Kobe. Kobe alimbeba Konokono mgongoni mwake. Walitafuta,  lakini hawakuiona.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_6', 3, NULL, 'assets/topic/asb/9216.png', 'Baada ya muda mfupi, walimwona Ndege. "Mnatafuta nini?" Ndege aliwauliza. "Ninatafuta nyumba yangu. Umeiona?" Kobe alisema. "La, sijaiona, lakini nitakusaidia kuitafuta!" Ndege akajibu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_7', 3, NULL, 'assets/topic/asb/9213.png', 'Ndege aliyapapatua mabawa yake na kupaa. "Unakwenda kwa kasi sana." Kobe alilalamika. Ndege akamwambia, "Samahani, wacha nami pia niruke juu ya gamba lako."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_8', 3, NULL, 'assets/topic/asb/9223.png', 'Kobe aliwabeba Konokono na Ndege juu ya gamba lake. Walitafuta bila kuona ishara ya nyumba ya Kobe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_9', 3, NULL, 'assets/topic/asb/9218.png', 'Walipoendelea, walimwona Bunzi. "Mnatafuta nini?" Bunzi aliwauliza. "Ninatafuta nyumba yangu. Umeiona?" Kobe alijibu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_10', 3, NULL, 'assets/topic/asb/9219.png', 'Bunzi alisema, "La, sijaiona, lakini nitakusaidia kuitafuta!" Aliruka mgongoni mwa Kobe. Kobe aliwabeba Konokono, Ndege na Bunzi. Walitafuta,  lakini hawakuona nyumba yoyote.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_11', 3, NULL, 'assets/topic/asb/9220.png', 'Hatua chache, walikutana na Panya. Alikuwa akitengeneza shada zuri la maua. "Unatafuta nini?" Panya aliuliza. "Natafuta nyumba yangu. Umeiona?" Kobe alisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_12', 3, NULL, 'assets/topic/asb/9221.png', 'Panya alijibu, "La, sijaiona, lakini nitakusaidia kuitafuta!" Aliruka mgongoni mwa Kobe. "Subiri,  siwezi kuacha maua yangu." Panya alijinyoosha,  akayakusanya maua yake huku Kobe akimsubiri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_13', 3, NULL, 'assets/topic/asb/9222.png', 'Kobe aliendelea kutembea akiwabeba Konokono, Ndege, Bunzi na Panya mgongoni mwake. Walitafuta, lakini hawakuipata nyumba ya Kobe. Upepo uliyapeperusha maua ya Panya. Kobe alikuwa amechoka sana. Akasema, "Ninyi ni wazito."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_14', 3, NULL, 'assets/topic/asb/9224.png', 'Upepo uliongezeka. Anga likabadilika likawa jeusi. "Ah, iwapi nyumba yangu?" Kobe aliuliza. Punde, radi ilinguruma milimani na umeme ukaangaza. Mara mvua ikaanza kunyesha. "Ah, iwapi nyumba ya Kobe?" Konokono, Ndege, Bunzi na Panya waliuliza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_15', 3, NULL, 'assets/topic/asb/9227.png', 'Upepo ulizidi kuvuma na kuvuruga. Ghafla, upepo uliwapeperusha wote kutoka mgongoni mwa Kobe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_16', 3, NULL, 'assets/topic/asb/9226.png', 'Mvua ya mawe ikaanza kunyesha. Kwa uoga, Kobe alijikunja na kuingia ndani ya gamba lake. Mle ndani mlikuwa joto. "Kumbe, hii ndiyo nyumba yangu!" Kobe aliwaza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12692_17', 3, NULL, 'assets/topic/asb/9225.png', 'Konokono, Ndege, Bunzi na Panya walijikusanya chini ya gamba la Kobe wakatambua, "Ah, hii ndiyo nyumba ya Kobe!"', NULL);
