INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073', 2, 'Nyumbu', 'assets/topic/asb/17977.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_3', 3, NULL, 'assets/topic/asb/17968.png', 'Hapo zamani, kulikuwa na nyumbu mrembo.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_4', 3, NULL, 'assets/topic/asb/17969.png', 'Farasi mwenye umbo zuri alisema, "Lazima nimwoe Waridi."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_5', 3, NULL, 'assets/topic/asb/17970.png', 'Farasi alimwambia Waridi, "Twende mtoni pamoja."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_6', 3, NULL, 'assets/topic/asb/17971.png', 'Farasi alimwuliza Waridi kuwa mke wake.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_7', 3, NULL, 'assets/topic/asb/17972.png', 'Farasi alitaka kumfahamu Waridi zaidi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_8', 3, NULL, 'assets/topic/asb/17973.png', 'Waridi alijibu, "Mama yangu anaishi katika ikulu ya mfalme."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_9', 3, NULL, 'assets/topic/asb/17974.png', '"Dada yangu anaishi katika uwanja wa kanisa. Kwani humjui?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_10', 3, NULL, 'assets/topic/asb/17975.png', 'Aliongeza, "Shangazi yangu mrembo anaishi na mzee wa kijiji."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_11', 3, NULL, 'assets/topic/asb/17976.png', 'Wakati huo, punda mzee alipita. Alikuwa babake Waridi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_12', 3, NULL, 'assets/topic/asb/17977.png', 'Waridi hakumsalimu babake wala hakumtazama.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_13', 3, NULL, 'assets/topic/asb/17978.png', 'Punda yule mzee akauliza, "Kuna nini binti yangu?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_14', 3, NULL, 'assets/topic/asb/17979.png', 'Farasi alimwuliza Waridi, "Huyu punda mjinga ni nani?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_15', 3, NULL, 'assets/topic/asb/17980.png', 'Waridi alimjibu, "Mimi simjui."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_16', 3, NULL, 'assets/topic/asb/17981.png', 'Farasi alimpiga teke yule punda mzee.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_17', 3, NULL, 'assets/topic/asb/17982.png', 'Punda mzee alivunjika moyo.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21073_18', 3, NULL, 'assets/topic/asb/17984.png', 'Waridi hakumheshimu babake.
