INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23775_3', 3, NULL, 'assets/topic/asb/8661.png', 'olwatuka waliyo omuwala nga'' yitibwa Emily era nga wakyejo dala.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23775_4', 3, NULL, 'assets/topic/asb/7518.png', 'Buli nyina bweyamugambanga akole emirimu nga agaana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23775_5', 3, NULL, 'assets/topic/asb/8630.png', 'Olunaku lumu nyina yalwala. Era namutuma agende ewaa jajjawe amuletereyo kuddagala elye''njawulo, asobole okubeera ngawona. Olwe'' kyejo kya Emily, yasalao kukwata makubo ge.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23775_6', 3, NULL, 'assets/topic/asb/8559.png', 'Obudde bwaziba nga Emily tanadda, maama we yatandia okweralikirira.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23775_7', 3, NULL, 'assets/topic/asb/8558.png', 'Era bunambiro, nyina yakowola abatuuze bo'' kukyalo bamuyambeko okunonya Emily.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23775_8', 3, NULL, 'assets/topic/asb/8115.png', 'Yatya nyo kubanga kukyalo okwo kwaliko ekisodde ekyalyanga abawala ekiro.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23775_9', 3, NULL, 'assets/topic/asb/8643.png', 'Ngabatuze bakyanonya Emily, yeyali yeerira bulamu mukidiba. Era ngatafaayo nti obudde buzibye.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23775_10', 3, NULL, 'assets/topic/asb/8114.png', 'Obudde bwe'' kisodde okulya bwatuuka era nekitandika omuyiggo gwe'' mmere. Bwekyali kitambula kyatandika okuwunyiriza akawowo komuwala. Ngeno abatuuze bwebasembera kukidiba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23775_11', 3, NULL, 'assets/topic/asb/7584.png', 'Emily yalaba ekisodde ngakigya natandika okukaaba. Abatuze bawulira omulanga gwo'' muto era nebatandika okuduka nga bwe baddagyali.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23775_12', 3, NULL, 'assets/topic/asb/8025.png', 'Abatuze basisinkana Emily nga akyali mu kidiba, yali attidde ebitenkanika era nga atintima. Era okuva kwo'' lwo yayiga okuwuliriza nyina no'' kumusaamu ekitibwa.', NULL);
