INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22530', 2, 'Pa Nase buibui, na wanawake', 'assets/topic/asb/26450.png', 'Ursula  Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22530_3', 3, NULL, 'assets/topic/asb/26446.png', 'Hapo zamani, kulikuwa na buibui aliyeitwa Pa Nase. Alihisi njaa sana akaamua kutafuta chakula.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22530_4', 3, NULL, 'assets/topic/asb/26447.png', 'Pa Nase, alikuwa na wazo tofauti la kuweza kupata chakula. Alikumbuka kuwa mle kijijini, kulikuwa na ghala ambamo wanawake wa kijijini walikuwa wakihifadhi chakula chao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22530_5', 3, NULL, 'assets/topic/asb/26448.png', 'Alinyatia akaingia katika lile ghala na kuiba samaki na ndizi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22530_6', 3, NULL, 'assets/topic/asb/26449.png', 'Alipokuwa njiani akirudi, watoto waliona namna alivyotembea kwa njia isiyokuwa ya kawaida.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22530_7', 3, NULL, 'assets/topic/asb/26450.png', 'Akiwa na mifuko iliyojaa, Pa Nase aliogopa akataka kukimbia.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22530_8', 3, NULL, 'assets/topic/asb/26451.png', '"Hebu tumsagie kwenye lile jiwe la kijijini," Yalol, mmoja wa wanawake alipendekeza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22530_9', 3, NULL, 'assets/topic/asb/26452.png', 'Pa Nase alicheka kwa sauti akasema, "Babu yangu huishi kwenye lile jiwe. Kunisagia pale ni kama kunituma nyumbani. Asanteni!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22530_10', 3, NULL, 'assets/topic/asb/26453.png', '"Tumtupe motoni," Betty akasema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22530_11', 3, NULL, 'assets/topic/asb/26454.png', '"Tukijaze kikapu matunda, tumweke ndani kisha tukitupe mtoni," Yabana alipendekeza. Pa Nase alilia, "Mkifanya hivyo, mtaniua."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22530_12', 3, NULL, 'assets/topic/asb/26455.png', 'Pa Nase alipofika katikati ya mto, alisherehekea. Aliyachukua baadhi ya matunda na kuyala huku akiwacheka wale wanawake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22530_13', 3, NULL, 'assets/topic/asb/26456.png', 'Wanawake wale walijua kuwa Pa Nase alikuwa amewadanganya.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22530_14', 3, NULL, 'assets/topic/asb/26457.png', 'Pa Nase alisahau kuwa kulikuwa na wavu sehemu ya chini ya mto. Alinaswa kwenye wavu ule akawa ametegwa.
