INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8877_3', 3, NULL, 'assets/topic/asb/8864.png', 'Bwana Kio anaishi kijijini kwetu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8877_4', 3, NULL, 'assets/topic/asb/8865.png', 'Bwana Kio anapoenda kazini,  anauacha mlango wazi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8877_5', 3, NULL, 'assets/topic/asb/8866.png', 'Safi anaketi mlangoni na kuilinda nyumba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8877_6', 3, NULL, 'assets/topic/asb/8867.png', 'Mbwa wa jirani akiikaribia nyumba, Safi anamtisha.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8877_7', 3, NULL, 'assets/topic/asb/8868.png', 'Mbwa anatoroka na kurudi kwao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8877_8', 3, NULL, 'assets/topic/asb/8870.png', 'Jirani akiugusa malango wa nyumba, Safi anamwona.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8877_9', 3, NULL, 'assets/topic/asb/8873.png', 'Jirani anageuka haraka na kwenda zake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8877_10', 3, NULL, 'assets/topic/asb/8871.png', 'Safi anapohisi njaa, anajua la kufanya.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8877_11', 3, NULL, 'assets/topic/asb/8872.png', 'Safi anakula kutoka bakuli lake la bluu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8877_12', 3, NULL, 'assets/topic/asb/8874.png', 'Akimaliza, anaruka juu ya sofa na kulala.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8877_13', 3, NULL, 'assets/topic/asb/8876.png', 'Bwana Kio anarudi nyumbani jioni.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8877_14', 3, NULL, 'assets/topic/asb/8875.png', 'Bwana Kio na Safi wanaketi sebuleni.
