INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22065', 2, 'Paka mwerevu na Mbwa mjinga', 'assets/topic/asb/25483.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22065_3', 3, NULL, 'assets/topic/asb/25473.png', 'Hapo kale, kulikuwa na Paka mwerevu na Mbwa mjinga. Paka mwerevu aliishi vizuri katika nyumba ya kifahari.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22065_4', 3, NULL, 'assets/topic/asb/25474.png', '"Inawezekanaje familia tajiri kukuruhusu kuishi nyumbani kwao?" Mbwa mjinga alimwuliza  yule Paka mwerevu kwa hasira.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22065_5', 3, NULL, 'assets/topic/asb/25475.png', 'Paka mwerevu alijibu, "Ninaishi na watu kwa sababu mimi ni nadhifu. Ninajua kuzika kinyesi changu. Nikihitaji chakula, mimi husema, miau."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22065_6', 3, NULL, 'assets/topic/asb/25476.png', '"Je, mimi pia nikifanya hivyo wataniruhusu niishi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22065_7', 3, NULL, 'assets/topic/asb/25477.png', 'Kabla ya mazungumzo hayo, Mbwa mjinga alikuwa akitaka kumla Paka mwerevu. Baada ya mazungumzao, alisema, "Leo, sitakuumiza."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22065_8', 3, NULL, 'assets/topic/asb/25478.png', 'Mbwa mjinga alifuata ushauri aliopewa na Paka mwerevu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22065_9', 3, NULL, 'assets/topic/asb/25479.png', 'Mwenye nyumba alitoka akampiga kwa fimbo. "Huyu Paka amenidanganya. Nikimpata, nitamla." Mbwa mjinga alinung''unika.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22065_10', 3, NULL, 'assets/topic/asb/25480.png', 'Baadaye, Paka mwerevu alipokuwa akicheza nje ya nyumba, Mbwa mjinga alimwambia, "Wakati ule ulinidanganya. Nilibweke mlangoni ulivyonishauri na mwenye nyumba akanipiga. Sasa nitakula."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22065_11', 3, NULL, 'assets/topic/asb/25481.png', 'Paka mwerevu akamjibu, "Mbwa, nisikize. Ni sawa utanila. Lakini, kwanza niache nicheze. Mimi humuacha panya acheze kwanza ndivyo nimle."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22065_12', 3, NULL, 'assets/topic/asb/25482.png', 'Mbwa mjinga alimwuliza, "Panya huchezaje?" Paka akamjibu, "Mimi humwuliza ahesabu hadi tatu. Kisha humkamata anapokuwa anahesabu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22065_13', 3, NULL, 'assets/topic/asb/25483.png', 'Basi Mbwa mjinga akamwacha Paka mwerevu huru. Kisha akamwambia ahesabu hadi tatu. Paka mwerevu alitoroka akapanda juu ya mti.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22065_14', 3, NULL, 'assets/topic/asb/25484.png', 'Paka mwerevu akamjibu, "Sikiza Mbwa, hutawahi kunishika tena. Mimi si mjinga. Unaweza kuhesabu hadi mia moja, elfu au milioni, lakini hutawahi kunipata tena!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22065_15', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19402', 2, 'Paka na Panya', 'assets/topic/asb/18908.png', 'Susan Kavaya', 'FF9DEDE3');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19402_3', 3, NULL, 'assets/topic/asb/18908.png', 'Hapo zamani za kale, paka na panya walikuwa marafiki. Walifanya kazi na kucheza pamoja. Walioana na kuishi kwa furaha.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19402_4', 3, NULL, 'assets/topic/asb/18909.png', 'Siku moja, panya walisema kwa hofu, "Baadhi ya jamaa zetu wamepotea." "Sijamwona mjomba kwa muda," mmoja alisema. "Sijamwona shangazi," alisema mwingine. "Dadangu hayuko," alisema wa tatu. "Kakangu yuko wapi?" aliuliza wa nne.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19402_5', 3, NULL, 'assets/topic/asb/18910.png', 'Panya mmoja mzee alitikisa kichwa chake kwa huzuni. "Marafiki wapendwa, nina habari mbaya." Alisema. "Paka wanatula. Ni lazima tuwe waangalifu. Hivi karibuni watatula sisi sote."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19402_6', 3, NULL, 'assets/topic/asb/18911.png', 'Panya waliingiwa na woga mwingi. Panya wa kike waliwaambia watoto wao, "Msiende nje. Kuweni waangalifu paka wasije kuwashika."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19402_7', 3, NULL, 'assets/topic/asb/18912.png', 'Panya wote walikuwa waangalifu sasa. Walikaa katika mashimo yao na wala hawakwenda nje.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19402_8', 3, NULL, 'assets/topic/asb/18913.png', 'Sasa paka walikuwa na wasi wasi pia. Walisema, "Je,  panya wote wamekwenda wapi? Tunahisi njaa na hatuna chochote cha kula." "Wamejificha," paka mkubwa alisema. "Tunawezaje kuwakamata?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19402_9', 3, NULL, 'assets/topic/asb/18932.png', 'Paka mwingine alisema, "Tutawauliza tupange ndoa kati yao nasi. Sherehe ya harusi itawaleta panya pamoja. Nasi tutawashika wote wakati mmoja tuwale!" "Hilo ni wazo zuri sana," paka walisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19402_10', 3, NULL, 'assets/topic/asb/18933.png', 'Basi, paka waliwatuma wazee wao kwenda kuwaona panya. "Tungependa tuwe na ndoa kati ya mmoja wa binti zenu na mmoja wa wavulana wetu." Paka wazee waliwaambia panya.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19402_11', 3, NULL, 'assets/topic/asb/18934.png', 'Panya walisema, "Hatuwaamini. Mnataka kutula tu." Paka walijibu, "La! Sisi ni waaminifu na wenye mioyo safi. Tunataka tuwe marafiki. Hapo zamani wengine wetu waliwala, lakini hilo halifanyiki tena. Hatutawahi kuwala tena."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19402_12', 3, NULL, 'assets/topic/asb/18935.png', 'Panya walipowatazama paka, waliona kuwa wote walikuwa wakitabasamu. "Tumekubali ndoa ifanyike kati ya mmoja wa binti zetu na mmoja wa mvulana wenu." Panya walisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19402_13', 3, NULL, 'assets/topic/asb/18936.png', 'Paka walienda nyumbani kwa furaha. "Siku ya harusi itakuwa nzuri sana," walisema huku wakilamba midomo yao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19402_14', 3, NULL, 'assets/topic/asb/18937.png', 'Siku ya harusi, panya walikuwa bado na wasi wasi mwingi. "Kaeni karibu na mashimo yenu," waliwaonya wanao. "Tazameni, paka wanakuja. Iwapo watakuwa wazuri kwetu, tutakuwa wazuri kwao. Lakini wakijaribu kutula, tutakuwa tayari kutoroka."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19402_15', 3, NULL, 'assets/topic/asb/18938.png', 'Paka walitokezea barabarani wakiimba wimbo wa harusi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19402_16', 3, NULL, 'assets/topic/asb/18939.png', '"Je, unayasikia maneno ya wimbo huo wanaoimba?" Panya mzee alimwuliza panya mchanga. "Ndiyo," panya mchanga alijibu. Wimbo unasema, "Washikeni muwale wote! Washikeni muwale wote!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19402_17', 3, NULL, 'assets/topic/asb/18940.png', '"Basi ni lazima tuimbe pia," alisema panya mzee. Akaanza kuimba, "Kimbieni panya, kimbieni! Kimbieni panya, kimbieni!" Panya wote waliruka ndani ya mashimo yao wakajificha.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19402_18', 3, NULL, 'assets/topic/asb/18941.png', 'Paka waliwasili wakaulizana, "Je, yuko wapi Bi. Harusi?" Panya mzee alichomoza kichwa chake kutoka shimoni akawajibu. "Tumepata funzo. Maadui zako wakijifanya kuwa marafiki, ni lazima uwe tayari kutoroka."', NULL);
