INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15558', 2, 'Paka wa Selemeng', 'assets/topic/asb/12005.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15558_3', 3, NULL, 'assets/topic/asb/12005.png', 'Selemeng anawapenda paka. Kuna paka wengi nyumbani kwake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15558_4', 3, NULL, 'assets/topic/asb/12006.png', 'Selemeng ana paka mmoja mweusi. Paka huyu anakula nyama nyingi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15558_5', 3, NULL, 'assets/topic/asb/12007.png', 'Selemeng vilevile ana paka mmoja mnono. Paka huyu hula kila kitu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15558_6', 3, NULL, 'assets/topic/asb/12008.png', 'Paka wa tatu, alipanda mtini na akakwama huko.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15558_7', 3, NULL, 'assets/topic/asb/12028.png', 'Selemeng alimfuata paka akakwama naye mtini. Mamake Selemeng alilazimika kumsaidia kushuka chini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15558_8', 3, NULL, 'assets/topic/asb/12009.png', 'Selemeng pia ana paka wawili wavivu. Wanalala nje ya nyumba wakiota jua mchana kutwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15558_9', 3, NULL, 'assets/topic/asb/12019.png', 'Selemeng vilevile, ana paka watatu wanao shughuli nyingi. Usiku wanawashika panya jikoni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15558_10', 3, NULL, 'assets/topic/asb/12020.png', 'Selemeng ana mbwa anayeitwa Lirafi. Lirafi ana huzuni kwa sababu hana rafiki.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15558_11', 3, NULL, 'assets/topic/asb/12021.png', 'Paka wa Selemeng hawampendi Lirafi. Wanapomfukuza, Lirafi anatoroka nyumbani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15558_12', 3, NULL, 'assets/topic/asb/12022.png', 'Selemeng anapompata Lirafi, anamrudisha nyumbani. Paka hawfurahi Lirafi anaporejeshwa. Hawataki kumwona tena.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15558_13', 3, NULL, 'assets/topic/asb/12024.png', 'Selemeng ana paka wangapi?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15558_14', 3, NULL, 'assets/topic/asb/12023.png', 'Selemeng ana mbwa wangapi? Lirafi yuko wapi sasa?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15558_15', 3, NULL, NULL, 'Nyumbani kwenu kuna paka wangapi? Je, mna mbwa nyumbani kwenu?', NULL);
