INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22083', 2, 'Panya', 'assets/topic/asb/7506.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22083_3', 3, NULL, 'assets/topic/asb/7496.png', 'Hapo zamani za kale, palikuwa na panya wawili.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22083_4', 3, NULL, 'assets/topic/asb/7497.png', 'Panya yule mwingine aliishi nyumbani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22083_5', 3, NULL, 'assets/topic/asb/7498.png', 'Panya aliyeishi nyumbani alimwambia aliyeishi kichakani, "Unakula na kulala vibaya. Sisi tunalala na kula vizuri."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22083_6', 3, NULL, 'assets/topic/asb/7499.png', '"Njoo ututembelee siku moja ujionee." Panya aliyeishi nyumbani alisema. "Niko tayari kuja usiku wa leo." Panya aliyeishi kichakani alijibu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22083_7', 3, NULL, 'assets/topic/asb/7500.png', 'Wakati huo, mwenye nyumba alikuwa amekasirika sana. Alisema, "Hawa panya wanaokula chakula changu wataona!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22083_8', 3, NULL, 'assets/topic/asb/7501.png', 'Panya wale wawili waliingia mle nyumbani kula vinono.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22083_9', 3, NULL, 'assets/topic/asb/7502.png', 'Walipoufikia mtego, panya aliyeishi nyumbani alisema, "Hebu nionje hii sukari. Leo utajua vitamu tunavyokula hapa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22083_10', 3, NULL, 'assets/topic/asb/7503.png', 'Lakini hata kabla ya panya kuonja sukari, mtego uliteguka, pa! Panya alishikwa shingoni. Macho yake yalivimba kama mbilingani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22083_11', 3, NULL, 'assets/topic/asb/7504.png', 'Panya aliyeishi kichakani alitazama tu kwa mshangao.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22083_12', 3, NULL, 'assets/topic/asb/7505.png', 'Lakini, panya aliyeishi nyumbani hakuweza kumjibu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22083_13', 3, NULL, 'assets/topic/asb/7505.png', 'Panya aliyeishi kichakani aliondoka pale akisema, "Haa! Sitaweza kuvifurahia hivi vitamu anavyovisifu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22083_14', 3, NULL, 'assets/topic/asb/7506.png', 'Heri nyumbani kwako kubaya kuliko kwa jirani kuzuri.', NULL);
