INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21127', 2, 'Panya na Paka', 'assets/topic/asb/18908.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21127_3', 3, NULL, 'assets/topic/asb/18908.png', 'Hapo zamani, paka na panya walikuwa marafiki.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21127_4', 3, NULL, 'assets/topic/asb/18909.png', 'Panya walianza kupoteza baadhi ya jamaa zao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21127_5', 3, NULL, 'assets/topic/asb/18910.png', 'Panya mmoja mzee alisema, "Nadhani paka wanatula."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21127_6', 3, NULL, 'assets/topic/asb/18911.png', 'Akina mama waliwaambia watoto wao, "Kueni waangalifu. Msiende nje."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21127_7', 3, NULL, 'assets/topic/asb/18912.png', 'Panya wote walikuwa waangalifu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21127_8', 3, NULL, 'assets/topic/asb/18913.png', 'Paka walianza kuwa na njaa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21127_9', 3, NULL, 'assets/topic/asb/18932.png', 'Paka waliwatembelea panya.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21127_10', 3, NULL, 'assets/topic/asb/18933.png', 'Walisema, "Tungependa binti wenu mmoja aolewe kwa mvulana wetu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21127_11', 3, NULL, 'assets/topic/asb/18934.png', 'Panya waliwaambia paka, "Mnadanganya. Mnataka kutula."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21127_12', 3, NULL, 'assets/topic/asb/18935.png', 'Mwishowe, walikubali kuwa na harusi kati ya wanao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21127_13', 3, NULL, 'assets/topic/asb/18936.png', 'Paka walirudi nyumbani kwa furaha.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21127_14', 3, NULL, 'assets/topic/asb/18937.png', 'Siku ya harusi, panya walikuwa waangalifu sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21127_15', 3, NULL, 'assets/topic/asb/18938.png', 'Paka waliwasili wakiimba wimbo wa harusi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21127_16', 3, NULL, 'assets/topic/asb/18939.png', 'Wimbo ulisema, "Washikeni muwale."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21127_17', 3, NULL, 'assets/topic/asb/18940.png', 'Panya mzee alianza kuimba, "Kimbieni, panya, kimbieni."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21127_18', 3, NULL, 'assets/topic/asb/18941.png', 'Panya mzee aliwaambia paka, "Sisi nanyi si marafiki."', NULL);
