INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31418', 2, 'Petro na mbwa wake', 'assets/topic/asb/25201.png', 'Ursula  Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31418_3', 3, NULL, 'assets/topic/asb/25198.png', 'Hapo zamani, kulikuwa na mvulana kwa jina Petro. Alikuwa mwanafunzi mwerevu aliyeibuka wa kwanza darasani. Wazazi wake walijivunia kazi yake nzuri. Lakini, Petro alitamani kuwa na kaka, dada au hata kipenzi cha kucheza naye.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31418_4', 3, NULL, 'assets/topic/asb/25199.png', 'Siku moja, Petro alimwuliza mama, "Ninaweza kwenda nje kucheza? Nimekuwa nikisoma wakati huu wote. Ningependa sasa niende nicheze na marafiki zangu." Mamake alimjibu, "Ni sawa! Nenda, lakini usichelewe!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31418_5', 3, NULL, 'assets/topic/asb/25200.png', 'Alipokuwa akienda uwanjani, Petro aliona jambo lisilokuwa la kawaida. Alimwona mbwa mdogo mwenye madoadoa. Mbwa huyo alikuwa amekwama shimoni na alikuwa ameogopa. "Usiwe na wasiwasi, sitakuumiza. Nitakusaidia," Petro alimwambia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31418_6', 3, NULL, 'assets/topic/asb/25201.png', 'Petro alimtoa mbwa yule shimoni. Mbwa alibweka kwa furaha, akacheza cheza na kumlamba Petro. "Utakuwa mbwa wangu," Petro alisema kwa furaha.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31418_7', 3, NULL, 'assets/topic/asb/25197.png', 'Mbwa alikimbia akaenda mbali na Petro. Marafiki za Petro walipomwona mbwa akiwakaribia, walisema, "Mbwa huyu anaweza kutuuma." Walimpiga kwa mawe. Petro alipowaona, alikimbia akiwapigia kelele waache kufanya hivyo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31418_8', 3, NULL, 'assets/topic/asb/25202.png', 'Marafiki wale hawakumsikia Petro. Mbwa alikimbia haraka alivyoweza ili kuyaokoa maisha yake. Alijificha ili wavulana wale waliokuwa wakimfukuza wasimwone. Hawakumpata.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31418_9', 3, NULL, 'assets/topic/asb/25203.png', 'Marafiki wa Petro walirudi uwanjani na kumkuta Petro akiwa analia. "Kuna nini?" mmoja wao aliuliza. "Mlimfukuza mbwa wangu!" Petro alieleza. Walishangaa na kusema, "Hatukujua kwamba yule alikuwa mbwa wako. Tunakuomba radhi. Twende tumtafute."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31418_10', 3, NULL, 'assets/topic/asb/25204.png', '"Hakuna maana ya kufanya hivyo. Hatutawahi kumpata," Petro alisema kwa huzuni. "Ndiyo, tutampta. Amka twende, tutampata mbwa wako. Tusipoteze matumaini," marafiki zake walimfariji.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31418_11', 3, NULL, 'assets/topic/asb/25205.png', 'Petro na marafiki zake walimtafuta mbwa kwa muda mrefu hadi wakachoka. Hatimaye, Petro alisema, "Kuna sehemu moja ambapo anaweza kuwa amejificha. Tusipompata huko, sitamtafuta tena. Twendeni mtoni."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31418_12', 3, NULL, 'assets/topic/asb/25206.png', 'Mbwa alipowaona wavulana wakija, aliogopa tena. Aljaribu kuvuka mto lakini vilevile aliyaogopa maji. Alikuwa karibu kuruka majini, wakati Petro alisema, "Acha, usiruke!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31418_13', 3, NULL, 'assets/topic/asb/25207.png', 'Mbwa alipogeuka na kumwona Petro, alimkimbilia. Petro alimkubatia mbwa wake naye mbwa akamlamba usoni. "Rafiki yangu! Nilidhani kwamba umepotea kabisa!" Petro alilia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31418_14', 3, NULL, 'assets/topic/asb/25208.png', 'Walipokuwa uwanjani, Petro aliwashauri marafiki zake. "Kabla ya kuamua kutenda jambo, tafadhali fikirieni kinachoweza kutokea. Mnaweza kuwaumiza wenzenu." Marafiki zake waliwaza juu ya maneno yake kisha wakasema, "Tusamehe. Mbwa huyu atakuwa rafiki yetu pia."', NULL);
