INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21809_4', 3, NULL, 'assets/topic/asb/25199.png', 'Siku moja, Petros alipata ruhusa ya kwenda kucheza na marafiki zake.', NULL);
