INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21809_10', 3, NULL, 'assets/topic/asb/25204.png', 'Petros hakuwa na matumaini.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21809_11', 3, NULL, 'assets/topic/asb/25205.png', 'Walimtafuta kila mahali.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21809_12', 3, NULL, 'assets/topic/asb/25206.png', 'Mbwa alipowaona, alitaka kuruka mtoni.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21809_13', 3, NULL, 'assets/topic/asb/25207.png', 'Mbwa alimkimbilia Petros.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21809_14', 3, NULL, 'assets/topic/asb/25208.png', 'Marafiki za Petros walimwambia, "Tusamehe. Atakuwa rafiki yetu pia."', NULL);
