INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24422', 2, 'rafiki bora zaidi', 'assets/topic/asb/25842.png', 'Angela Mbalu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24422_3', 3, NULL, 'assets/topic/asb/1497.png', 'Mara moja kwa wakati, kulikuwa na marafiki tatu bora; Jude, Becky na Rachael.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24422_4', 3, NULL, 'assets/topic/asb/19328.png', 'Daima walifanya kazi zao za nyumbani pamoja; kwa kweli walikwenda nyumbani kwa kila mmoja na kusaidiana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24422_5', 3, NULL, 'assets/topic/asb/19336.png', 'Wazazi wao walifurahia sana.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24422_6', 3, NULL, 'assets/topic/asb/13497.png', 'Siku moja, walikwenda kisima, na walikaa muda mrefu sana huko. Walisahau kuwa ilikuwa ni kuchelewa. Michezo waliyocheza walikuwa tu nzuri kuwasaidia kukumbuka kwamba walipaswa kwenda nyumbani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24422_7', 3, NULL, NULL, 'Wao hata wamesahau juu ya onyo walilopewa juu ya kile kilichotokea wakati mtu alipokwisha kuchelewa vizuri. Je! Unaweza kufikiria nini onyo hilo lilikuwa?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24422_8', 3, NULL, NULL, 'Ni tu wakati watu walianza kuondoka vizuri moja kwa moja, wakakumbuka kwamba ilikuwa ni kuchelewa na walihitaji kwenda nyumbani pia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24422_9', 3, NULL, 'assets/topic/asb/931.png', 'Hata hivyo, wakati wa safari zao nyumbani, nyasi ziliwafunika!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24422_10', 3, NULL, NULL, 'Walipokuwa bado wanajitahidi kwenda nje, waliwafunga kabisa! Na wao kabisa kutoweka!
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24422_11', 3, NULL, 'assets/topic/asb/25390.png', 'Mapema asubuhi, mmoja wa wanawake wa kijiji akaenda kwenye kisima ili alichele maji.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24422_12', 3, NULL, 'assets/topic/asb/26427.png', 'Aliwasaidia kuondoka kwenye majani yaliyotajwa. Na mara moja wakawapeleka kwenye baraza la kijiji. Na kila mtu alikuwa na furaha ya kuwaona.
