INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9527_3', 3, NULL, 'assets/topic/asb/2444.png', 'Nilipoamka asubuhi,  nilikula kifungua kinywa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9527_4', 3, NULL, 'assets/topic/asb/2450.png', 'Nilikimbia kwenda kukutana na rafiki yangu Muthoni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9527_5', 3, NULL, 'assets/topic/asb/2452.png', 'Pindi nilipompata Muthoni,  tulisahau kila kitu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9527_6', 3, NULL, 'assets/topic/asb/1191.png', 'Mimi na Muthoni tulicheza michezo tofauti karibu na nyumba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9527_7', 3, NULL, NULL, 'Tulifanya kila kitu pamoja.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9527_8', 3, NULL, 'assets/topic/asb/1514.png', 'Tulikubaliana kufanya bidii katika masomo yetu na kufuzu vyema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9527_9', 3, NULL, 'assets/topic/asb/1517.png', 'Lakini siku moja,  Muthoni hakufika shuleni.  Nilimsubiri lakini,  hakufika.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9527_10', 3, NULL, 'assets/topic/asb/2481.png', 'Siku ile niliporudi nyumbani,  nilimwambia mama kwamba Muthoni hakuja shuleni siku hiyo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9527_11', 3, NULL, 'assets/topic/asb/8666.png', 'Niliendelea na masomo yangu lakini nilimkosa Muthoni sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9527_12', 3, NULL, 'assets/topic/asb/8641.png', 'Nilimtafuta Muthoni kila mahali.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9527_13', 3, NULL, 'assets/topic/asb/1812.png', 'Sasa nimekuwa msichana mkubwa lakini ninamkosa rafiki yangu Muthoni.', NULL);
