INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31547', 2, 'Rafiki ya Atieno', 'assets/topic/asb/26422.png', 'Ursula  Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31547_3', 3, NULL, 'assets/topic/asb/26422.png', 'Kuliishi msichana mdogo katika kijiji kimoja karibu na ziwa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31547_4', 3, NULL, 'assets/topic/asb/26423.png', 'Babake Atieno alikuwa mvuvi mashuhuri. Alikuwa na mtumbwi wake wa kuvua samaki.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31547_5', 3, NULL, 'assets/topic/asb/26424.png', 'Atieno aliwatazama wavulana wakicheza mpira wa miguu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31547_6', 3, NULL, 'assets/topic/asb/26425.png', 'Wasichana nao walimwambia Atieno, "Miguu yako ni mirefu mno." Atieno alihuzunika.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31547_7', 3, NULL, 'assets/topic/asb/26426.png', 'Siku moja, jua halikuchomoza. Jogoo hawakuwika, ndege hawakuimba na watoto hawakwenda shuleni. Hata babake hakwenda kuvua samaki.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31547_8', 3, NULL, 'assets/topic/asb/26427.png', 'Atieno alienda kuwaambia watoto wengine namna alivyohuzunika. Lakini, walimcheka, "Labda jua limefariki. Au pengine limekutoroka."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31547_9', 3, NULL, 'assets/topic/asb/26428.png', 'Atieno alihuzunika sana hata akakimbia kwenda nyumbani. Aliugonga mpira wa kakake kwa makosa nao ulikigonga kibuyu cha mafuta.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31547_10', 3, NULL, 'assets/topic/asb/26429.png', 'Atieno aliwaza, "Nitaucheza mpira huu hadi rafiki yangu jua litakaporudi."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31547_11', 3, NULL, 'assets/topic/asb/26430.png', 'Atieno alipofika nje, aliuweka mpira chini akawaza, "Ninaweza kucheza kama wao wanavyocheza."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31547_12', 3, NULL, 'assets/topic/asb/26431.png', 'Kila mmoja akitazama juu, mpira ulitokomea kwenye mawingu mazito.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31547_13', 3, NULL, 'assets/topic/asb/26432.png', 'Ghafla, mawingu yaliyokuwa mazito yalipungua na jua likatokezea. Kijiji hicho kikawa hai tena.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('31547_14', 3, NULL, 'assets/topic/asb/26433.png', 'Atieno hakuweza kuamini kwamba alikuwa amelirejesha jua kijijini. Lakini zaidi ya yote, jua lilikuwa limerudi maishani mwake.
