INSERT INTO `card` (id, type, title, header, content, option) VALUES ('6718', 2, 'Rafiki yangu Koko', 'assets/topic/asb/2485.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('6718_3', 3, NULL, 'assets/topic/asb/2486.png', 'Nilipokuwa wa umri wa miaka mitano, mjombangu alinipa zawadi ya ajabu sana. Alinipa mbwa mdogo wa wiki tatu! Siku mjomba aliniletea mbwa huyo,  nilifurahi sana. Nilijua kuwa nilikuwa nimempata rafiki.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('6718_4', 3, NULL, 'assets/topic/asb/2487.png', 'Nilipomweka chini, mjomba alinigeukia na kuniuliza,  "Utamwitaje?" Nilimwangalia mbwa wangu kisha nikasema, "Ataitwa KoKo." Mjombangu alishangaa sana akaniuliza, "Mbona unamwita Koko?" Nilimjibu,  "Kwa sababu rangi yake ni kama kakao."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('6718_5', 3, NULL, 'assets/topic/asb/2488.png', 'Nilimwuliza mjomba iwapo Koko alizaliwa peke yake. Mjomba alisema kwamba alikua na nduguye aliyemfanana kabisa. Nilienda kumwona nduguye Koko.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('6718_6', 3, NULL, 'assets/topic/asb/2485.png', 'Koko alipomwona nduguye, alichangamka sana. Wawili hao waliweka vichwa vyao pamoja kama waliokuwa wakiongea.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('6718_7', 3, NULL, 'assets/topic/asb/2491.png', 'Nilimwuliza mjombangu kama ningeishi na Koko na nduguye. Mjomba alikimya kidogo kisha akasema,  "Ni sawa wewe kuwaweka wote wawili." Hata hivyo,  alisema, "Lakini lazima uwalinde vizuri." Kwa hivyo kila jioni, nilicheza nao na kuwafanyisha mazoezi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('6718_8', 3, NULL, 'assets/topic/asb/2490.png', 'Miezi michache baadaye, Koko na nduguye walikuwa wamekua wakubwa. Walikuwa wenye nguvu na afya. Nami singeweza kuwachunga peke yangu tena.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('6718_9', 3, NULL, 'assets/topic/asb/2492.png', 'Nilimwuliza mjombangu kumrudisha nduguye Koko kwake. Koko alipoachwa peke yake, alihuzunika mno. Alionekana kama ananiuliza, "Kwa nini ulifanya hivyo?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('6718_10', 3, NULL, 'assets/topic/asb/2489.png', 'Koko alikoma kucheza. Wakati mwingine alikataa kwenda nami matembezini akipendelea tu kulala nje ya nyumba yetu. Wakati mwingine alikula chakula kidogo sana. Nilikuwa na wasiwasi. Nilijiuliza vipi ningeweza kumsaidia Koko?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('6718_11', 3, NULL, NULL, 'Siku moja mjombangu alitutembelea. Niliposikia sauti yake, nilifanya haraka kwenda nje. Kabla ya kumwona,  nilisikia mbwa wakibweka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('6718_12', 3, NULL, 'assets/topic/asb/2493.png', 'Nyuma ya jikoni, Koko na nduguye walikuwa wakicheza!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('6718_13', 3, NULL, 'assets/topic/asb/2494.png', 'Nilifurahi sana hadi sikujua nimkumbatie nani kwanza, Koko au mjombangu. Nilijua kwamba Koko angerejelea ile hali yake ya kwaida. Tungeanza tena kufanya mazoezi pamoja!', NULL);
