INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22078', 2, 'Rafiki yangu, Adui  yangu', 'assets/topic/asb/25536.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22078_3', 3, NULL, 'assets/topic/asb/25535.png', 'Katika mtaa wa Mfalele, kulikuwa na mvulana aliyeitwa Bobo. Aliishi na wazazi wake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22078_4', 3, NULL, 'assets/topic/asb/25536.png', 'Bobo aliwaheshimu wazazi wake na watu wote mtaani kwao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22078_5', 3, NULL, 'assets/topic/asb/25537.png', 'Jumatatu ni siku inayokuwa na shughuli nyingi pale mtaani. Watoto hujitayarisha kurudi shuleni baada ya wikendi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22078_6', 3, NULL, 'assets/topic/asb/25538.png', 'Jeuri alipenda kupigana na kuwachokoza wengine. Mara nyingi alikosa kuhudhuria shule. Alikuwa mvulana mtundu aliyecheza Kamari na kuandamana na watu wengine waovu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22078_7', 3, NULL, 'assets/topic/asb/25539.png', 'Bobo alianza kuwachokoza watu. Pia, hakuwaheshimu wazazi wake na watu wengine.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22078_8', 3, NULL, 'assets/topic/asb/25540.png', 'Bobo hakuyajali maisha yake. Aliambukizwa ugonjwa wa zinaa. Alipoanza kukonda, Jeuri alimfanya mzaha na kuukatiza urafiki wao.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22078_9', 3, NULL, 'assets/topic/asb/25541.png', 'Bobo alitafakari juu ya maisha yake akaamua kubadilika. Aligundua kwamba alikuwa amefanya urafiki na adui.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22078_10', 3, NULL, 'assets/topic/asb/25542.png', 'Mwanzoni, Jeuri alikasirika alipomwana Bobo akibadilika na kuwa mwema.
