INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10202_3', 3, NULL, 'assets/topic/asb/9465.png', 'Mimi na babangu tulienda mjini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10202_4', 3, NULL, 'assets/topic/asb/9466.png', 'Nlimwona mwanamke. Alikuwa amevaa nguo ya rangi ya chungwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10202_5', 3, NULL, 'assets/topic/asb/9469.png', 'Alijifunga mshipi mkubwa mwekundu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10202_6', 3, NULL, 'assets/topic/asb/9470.png', 'Alibeba kibeti cha rangi ya chungwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10202_7', 3, NULL, 'assets/topic/asb/9939.png', 'Alivaa viatu vya rangi ya chungwa. Vilikuwa na visigino virefu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10202_8', 3, NULL, 'assets/topic/asb/9940.png', 'Alikutana na msichana mdogo na mbwa wake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10202_9', 3, NULL, 'assets/topic/asb/9474.png', 'Alipozirekebisha nywele zake, kibeti chake kilianguka chini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10202_10', 3, NULL, 'assets/topic/asb/9942.png', 'Aliinama kwa utaratibu kukiokota kibeti chake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10202_11', 3, NULL, 'assets/topic/asb/9475.png', 'Vipuli vyake virefu vya rangi ya chungwa vilionekana.', NULL);
