INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10442_8', 3, NULL, 'assets/topic/asb/9843.png', 'Rangi ya tatu ni manjano.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10442_9', 3, NULL, 'assets/topic/asb/9844.png', 'Yai pingu lina rangi ya manjano.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10442_10', 3, NULL, 'assets/topic/asb/9846.png', 'Rangi ya nne ni kijani.', NULL);
