INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574', 2, 'Refi awaosha kuku wake', 'assets/topic/asb/986.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_3', 3, NULL, 'assets/topic/asb/1003.png', 'Ilikuwa harusi ya kwanza katika familia ya Tenana, na Refi alikuwa amechangamka sana. "Nitakuwa msaidizi wa Elsa nikiwa nimevaa nguo na viatu vipya! Refi aliwaelezea kuku wake. Aliwaelezea kuku wake kila kitu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_4', 3, NULL, 'assets/topic/asb/1011.png', '"Refi, waondoshe hapo hao kuku wachafu!" Mamake alipiga kelele, "Siwataki popote karibu na hema la harusi."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_5', 3, NULL, 'assets/topic/asb/989.png', 'Refi aliwafukuza kuku kutoka kwenye hema. "Tokeni enyi viumbe washienzi," alisema. "Sitaki mliwe wakati wa harusi ya Elsa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_6', 3, NULL, 'assets/topic/asb/1013.png', '"Mama asema kwamba hii itakuwa harusi ya kufana zaidi katika kijiji cha Mailisaba," Refi aliwaambia kuku wake. "Pia anasema kwamba ninaweza kuwasaidia wasichana wale wakubwa kupika." Kuku hawakumsikiliza hata kidogo.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_8', 3, NULL, NULL, 'Mamake Refi alijivunia nyumba yake na alitaka iwe safi siku ya harusi. Alisafisha, akaosha na kusugua kila kilochokuwamo. Alipomaliza, hapakuwa na chembe cha vumbi popote.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_9', 3, NULL, 'assets/topic/asb/1018.png', 'Refi aliwafuata kuku wake hadi kwenye jua. "Mama anasema ukweli, mnaaibisha," aliwakemea.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_10', 3, NULL, 'assets/topic/asb/993.png', 'Refi alimweka kuku wa kwanza ndani ya ndoo ya maji. "Kaa wima, ewe kiumbe shenzi," alipiga kelele. "Haitachukua muda mrefu kukuosha!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_11', 3, NULL, 'assets/topic/asb/1000.png', 'Refi alipopangusa pua na macho ya kuku, ghafla kuku alikuwa dhaifu akaanguka kando ya ndoo. "Huu sio wakati wa kulala," Refi alisema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_12', 3, NULL, 'assets/topic/asb/994.png', 'Aliwaosha kuku wote wanane na kuwaweka mstari mmoja juu ya nyasi ili wakauke. Hakuna yeyote aliyepapatua hata unyoya mmoja.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_13', 3, NULL, 'assets/topic/asb/990.png', 'Shangazi zake Refi, yaani Mama Netty na Mama Teddy, walikuwa wakitayarisha harusi ya Elsa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_14', 3, NULL, 'assets/topic/asb/995.png', 'Mama Netty aliwaona kuku wa Refi wakiwa wanakauka. "Hiki ni chakula kizuri kwangu. Nitawapeleka nyumbani!" Alijiambia.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_15', 3, NULL, NULL, 'Mama Teddy aliamua kutayarisha kitoweo alichokipenda. Alikwenda katika bustani ya maboga. Alikiona kifurushi cha Mama Netty chini ya mboga.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_16', 3, NULL, 'assets/topic/asb/997.png', '"Ninajua," alisema hatimaye, "nitawaweka paani!" Mama Teddy alipanda juu akawaweka kuku katika mstari kwenye paa la nyumba ya nyasi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_17', 3, NULL, NULL, 'Siku iliyofuata, jua lilichomoza kwa wakati mzuri kwa harusi. Refi alikuwa wa kwanza kuamka na kwenda kuwatazama kuku wake.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_18', 3, NULL, 'assets/topic/asb/1047.png', 'Harusi ya Elsa ilikuwa ya kufana. Wale kuku hawakuamka kutoka paani. Kwa hivyo, hawakujiunga na wasichana wasaidizi wa bi-harusi walipoingia wakicheza. Hakuna kuku yeyote aliyetikisika wakati kwaya ya kanisa iliimba kwa sauti ya juu nyimbo nzuri za kuvutia. Walikuwa bado wanalala juu ya paa wakati Padre alihubiri. Hawakutikisa hata unyoya wakati nguruwe na babu yake Refi walipokoroma mahubiri yalipoendelea.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_19', 3, NULL, 'assets/topic/asb/999.png', 'Hakuna hata mguu mmoja wa kuku uliotetemeka wakati kondoo waliranda na kuingia ndani ya hema wakiwa karibu kuangusha keki ya harusi. Wakati babake bwana harusi alipokuwa katikati ya hotuba yake ndipo mambo yalianza kubadilika paani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_20', 3, NULL, NULL, 'Kuku wa kwanza aliyanyoosha mabawa yake na kuruka kwenye kifua cha Mamake Molly. Mama Teddy aliyekuwa amekaa karibu naye alianza kucheka. Kuku mwingine alirukia kitambaa kipya cha Mama Netty. Watu waliokuwa kwenye meza nyingine walizuia kicheko. Mwanamke aliyekaa karibu na Mama Netty aliweka kichwa chake chini ya meza,  "Ai, aai, aaaaii, he, he, heeeeeeee!" Alicheka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_21', 3, NULL, 'assets/topic/asb/1002.png', 'Wale kuku wote waliamua kujiunga na wale wawili wa kwanza. Hungeweza kumwona Mama Netty kwa sababu alikuwa amefunikwa na kuku hao wote!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_22', 3, NULL, NULL, 'Wageni walicheka kwa mayowe. Wanaume walilazimika kuzishika tumbo zao. Wanawake waliviringika katika viti vyao. Vijana walijishikilia kwa wenzao. Akina nyanya hawangeweza kupumua kwa ajili ya kucheka. Akina babu walitikisa fimbo zao. Kisha shangazi hao wawili waliangaliana na kuanza kucheka pia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_23', 3, NULL, 'assets/topic/asb/1046.png', 'Mama Teddy aliufungua mdomo wake wazi kwa kicheko. Mama Netty alikirusha kichwa chake nyuma na kucheka hadi mashavu yakatikisika. Refi hakuamini!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_24', 3, NULL, NULL, 'Wageni wote walikubaliana kwamba hiyo ilikuwa harusi nzuri zaidi iliyowahi kufanyika kijijini Mailisaba. "Ah, mna bahati sana! Refi aliwaambia kuku wake alipokuwa anawaweka kwenye chumba chao usiku ule, "Mama anasema kuwa hatawahi kuwaweka kwenye chungu chake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2574_25', 3, NULL, 'assets/topic/asb/1048.png', '"Lakini, tazama jinsi mlivyo wachafu tena," aliwaambia, "Nadhani nitalazimika kuwaosha tena kesho!"', NULL);
