INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22747', 2, 'Robo za Bwana Hadaa', 'assets/topic/asb/15721.png', 'Ursula  Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22747_3', 3, NULL, 'assets/topic/asb/15714.png', 'Ni karibu wakati wa mapumziko katika shule ya Msingi ya Siyafunda. Wamiliki wa maduka mawili yaliyo pale shuleni wanatayarisha chakula cha mchana ili kuwauzia wanafunzi wenye njaa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22747_4', 3, NULL, 'assets/topic/asb/15715.png', 'Dukani kwa Bwana Mkweli mikate imekatwa tayari kutengeneza robo zitakazonunuliwa na wanafunzi. Anakata sehemu nyororo iliyo katikati kisha anaweka ndani mayai. Mkweli pia anaweka vijiko viwili vya kachumbari iliyotayarishwa na mkewe. Yeye hupenda kujigamba kwamba robo za mkate anazouza zina mayai ndani na juu kuna kachumbari!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22747_5', 3, NULL, 'assets/topic/asb/15716.png', 'Katika duka la Hadaa, mayai karibu yawe tayari, lakini bado anakata mikate kutengeneza robo. Ana wasiwasi kwa sababu biashara hayiendi vizuri dukani kwake. "Ilianza vizuri," Bwana Hadaa anawaza. "Lakini, sasa ninapata wateja wachache sana. Ingawa ninapunguza gharama yangu kwa kuikata mikate sehemu tano badala ya nne."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22747_6', 3, NULL, 'assets/topic/asb/15717.png', 'Jabu na Zorina wananunua chakula cha mchana. Zorina ananunua kutoka kwa Bwana Hadaa. Jabu naye ananunua kutoka kwa Bwana Mkweli. Jabu anaamini kuwa robo anazouza Bwana Mkweli zina mayai na kachumbari nyingi kuliko zile anazouza Bwana Hadaa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22747_7', 3, NULL, 'assets/topic/asb/15718.png', 'Foleni iliyo nje ya duka la Bwana Mkweli ni ndefu zaidi. Zorina anamsubiri Jabu. Kisha marafiki hao wanaketi pamoja kwenye kivuli kufurahia robo zao za mkate.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22747_8', 3, NULL, 'assets/topic/asb/15719.png', 'Zorina anona kitu kinachomfanya aukunje uso wake. "Unatazama nini?" Jabu anauliza. Zorina anajibu, "Robo yako! Inaonekana kubwa kuliko yangu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22747_9', 3, NULL, 'assets/topic/asb/15720.png', 'Wanazitazama robo zao kwa makini. Ni kweli kuwa robo ya Jabu ni kubwa kuliko ya Zorina. Zorina anasema, "Hakika, hapa kuna kitu kisicho sawa. Ama hii ndiyo sababu duka la Bwana Hadaa halina tena wateja wengi." Jabu anajibu, "Nilisikia kwamba baadhi ya watoto walimwona Bwana Hadaa akiukata mkate vipande vitano vya kutengeneza robo zake!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22747_10', 3, NULL, 'assets/topic/asb/15721.png', 'Nora anawasikia Jabu na Zorina wakiongea naye anataka kujua tofauti ya kuukata mkate vipande vitano. Zorina na Jabu wanajibu pamoja, "Kipande cha robo tunachouziwa ni robo ya mkate!" Nora anaonekana kutoelewa, kwa hivyo, Zorina anamweleza, "Lazima mkate ukatwe vipande vinne vilivyo sawa ili kila kipande kiwe robo ya mkate mzima."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22747_11', 3, NULL, 'assets/topic/asb/15722.png', 'Nora amekasirika kwa sababu yeye pia hununua kwa Bwana Hadaa. "Kwa hivyo anatuibia, sivyo!" anasema kwa sauti kubwa. Zorina anaamua kurudi kwa Bwana Hadaa kulalamika. Anatembea huku amebeba robo ya mkate wake juu. Nora anamfuata nyuma haraka. Jabu anashusha pumzi kisha anawafuata marafiki zake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22747_12', 3, NULL, 'assets/topic/asb/15723.png', 'Bwana Hadaa anaudhika anapoona kuwa wao sio
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22747_13', 3, NULL, 'assets/topic/asb/15724.png', '"Je, ni kweli Bwana Hadaa? Umekuwa ukituhadaa!" Nora analia. Wakati huo baadhi ya watoto wamewafuata hadi pale dukani. Wao pia wanasikiliza shutuma anazopata Hadaa. Hadaa anawapungia mkano kwa madharau, "Ni nini hiki mnachosema kunihusu? Mimi si mtu wa aina hiyo! Ninataka mwondoke dukani kwangu mara moja!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22747_14', 3, NULL, 'assets/topic/asb/15725.png', 'Zorina anasema kimya kimya, "Tutamwambia kila mtu kuhusu kile unachofanya. Ujumbe huo utaenea kama moto shuleni kote. Utalazimika kulifunga duka lako. Hakuna atakayenunua kutoka kwako!" Bwana Hadaa anawatazama watoto wale watatu wanaosimama mbele yake. Kisha anautazama umati unaoendelea kuongezeka nje ya duka lake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22747_15', 3, NULL, 'assets/topic/asb/15726.png', 'Mwishowe, Bwana Hadaa anasema, "Vyema, nitakurejeshea pesa zako." Jabu anasema, "Pia, ahidi kwamba utaukata mkate vipande vinne kuzitengeneza robo." Bwana Hadaa anaahidi kwamba atafanya hivyo. Kisha anasafisha koo yake na kusema kwa sauti ili kila mmoja asikie, "Pia, ninaahidi kwamba nitaongeza kiasi cha kachumbari na mayai kwenye robo za mkate."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22747_16', 3, NULL, 'assets/topic/asb/15727.png', 'Bwana Hadaa kweli aliongeza kiasi cha mayai na kachumbari kwenye robo za mkate alizotengeneza. Baadaye, watoto zaidi walikuwa wakinunua kutoka kwake na duka lake lilianza kupata faida. Aliweza kumwajiri mtu wa kumsaidia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22747_17', 3, NULL, 'assets/topic/asb/15728.png', 'Sasa, kuna ushindani mzuri kati ya Bwana Hadaa na Bwana Mkweli. Kila mtu anafurahi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22747_18', 3, NULL, 'assets/topic/asb/15729.png', 'Andika tafsiri ya picha uionayo hapa.', NULL);
