INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8712', 2, 'Sababu ya chura kuwa na sura mbaya', 'assets/topic/asb/7077.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8712_3', 3, NULL, 'assets/topic/asb/7069.png', 'Miaka mingi iliyopita, Mjusi na Chura walikuwa marafiki. Siku moja waliamua kuwatafuta wasichana wapenzi jijini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8712_4', 3, NULL, 'assets/topic/asb/7070.png', 'Chura alimwonea Mjusi wivu. Mjusi alikuwa na ngozi iliyometameta na ya kuvutia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8712_5', 3, NULL, 'assets/topic/asb/7071.png', 'Chura alimwuliza, "Ni kitu gani ulichofanya ukawa mrembo hivi? Hebu, nitazame, nina sura mbaya mno. Ninawezaje kuirembesha ngozi yangu?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8712_6', 3, NULL, 'assets/topic/asb/7072.png', 'Mjusi alimjibu, "Sikiliza! Tia maji chunguni kisha ukiweke mekoni. Halafu u..."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8712_7', 3, NULL, 'assets/topic/asb/7073.png', 'Hakumaliza usemi wake. Chura alimjibu, "Ninajua tayari, najua. Fyata ulimi, rafiki yangu." Kisha aliondoka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8712_8', 3, NULL, 'assets/topic/asb/7074.png', 'Chura alipofika nyumbani, aliyatia maji chunguni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8712_9', 3, NULL, 'assets/topic/asb/7075.png', 'Maji yalipoanza kuchemka, alijitumbukiza ndani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8712_10', 3, NULL, 'assets/topic/asb/7076.png', 'Ngozi yake ikachomeka na mkia wake ukakatika!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8712_11', 3, NULL, 'assets/topic/asb/7077.png', 'Na badala ya kumetameta na kuwa mrembo kama Mjusi, alikuwa na sura mbaya zaidi!', NULL);
