INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2197', 2, 'Samaki Mkarimu', 'assets/topic/asb/1840.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2197_3', 3, NULL, 'assets/topic/asb/1841.png', 'Hapo zamani za kale, Mpunzi na mkewe waliishi na mtoto wao mmoja wa kike aliyeitwa Nosisa. Mpunzi alitaka mtoto mvulana, lakini yeye na mkewe hawangeweza kupata watoto wengine. Mpunzi alimlaumu mkewe kwa kutompa mtoto wa kiume.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2197_4', 3, NULL, 'assets/topic/asb/1843.png', 'Siku moja, Mpunzi alimchapa mkewe. Mkewe alitoroka akaenda nyumbani kwao. Alihuzunika kwani Mpunzi hakumruhusu aende na Nosisa. Mpunzi alimdhulumu bintiye namna alivyomdhulumu mkewe. Nosisa aliishi maisha ya huzuni sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2197_5', 3, NULL, 'assets/topic/asb/1844.png', 'Mpunzi alioa mke mwingine. Alitumaini kwamba mke huyo mpya angemzalia mtoto mvulana. Maisha ya Nosisa sasa yalikuwa magumu zaidi babake alipooa tena. Mpunzi alimtetesha na kumlazimisha kumtumikia mke wake mpya. Nosisa alifanya kazi kama mtumwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2197_6', 3, NULL, NULL, 'Mpunzi aliona fahari sana kutokana na mifugo wake. Alizoea kutazama mifugo wake wakila nyasi uwanjani. Alihisi kuwa alikuwa ametosheka sana na mali yake. Wengi wa wafanyakazi wa Mpunzi walisafisha makao yake na kuwachunga mifugo wake. Walikama maziwa ambayo Mpunzi aliwauzia wanakijiji.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2197_8', 3, NULL, 'assets/topic/asb/1848.png', 'Mpunzi alimpenda sana mke wake mpya. Amchukulia kama malkia. Alikaa chumbani wakati wote huku akiwaamrisha Nosisa na wafanyakazi. Nosisa alirauka mapema kila siku kumtayarishia chai mamake wa kambo. Alimfagilia chumba chake na kumpikia, lakini mamake wa kambo alinung''unika kuwa chakula chake kilikuwa baridi, kuwa chai yake ilikuwa hafifu, na kwamba chumba chake kilikuwa kichafu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2197_9', 3, NULL, 'assets/topic/asb/1846.png', 'Siku moja Mpunzi alikasirika akamkaripia na kumgonga Nosisa kwa kijiti. Nosisa alitoroka akaenda mtoni alikokaa na kulia alasiri nzima. Ghafla, alisikia sauti kutoka majini. Alimwona Samaki aliyekuwa akitoa ile sauti. Nosisa alishtuka lakini Samaki alimtuliza, "Tafadhali, binti yangu, usitoroke, mimi ni mamako. Nimebadilika kuwa samaki ili babako asinitambue."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2197_10', 3, NULL, 'assets/topic/asb/1849.png', '"Una shida gani?" Samaki aliuliza. Nosisa alimweleza kuhusu maisha yake ya huzuni. "Usiwe na wasiwasi mwanangu. Nitakuletea chakula kila siku," Samaki alisema. Alimpa Nosisa mboga wakasherehekea halafu akatokomea. Nosisa alienda nyumbani akiwa anatabasamu. Alijua hatalala tena njaa kama ilivyokuwa desturi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2197_11', 3, NULL, 'assets/topic/asb/1857.png', 'Kila asubuhi Nosisa aliamka na kufanya kazi zote za nyumbani kama ilivyokuwa kawaida. Adhuhuri, alienda mtoni. Aliita kila wakati, naye Samaki alitokea akiwa amebeba chakula kitamu. Nosisa alifurahi na kumwelezea chochote. Alianza kuwa na afya nzuri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2197_12', 3, NULL, 'assets/topic/asb/1850.png', 'Nosisa alimtembelea Samaki kila siku hadi mamake wa kambo akaanza kumshuku. "Kuna jambo linaendelea kule mtoni, na ni lazima nitaligundua," aliwaza. Alasiri moja alimfuata Nosisa hadi mtoni. Alimwona Nosisa akimzungumzia Samaki na kupokea chakula kitamu. Siri ya Nosisa sasa ilikuwa imetambulika! "Lazima nifanye jambo kumhusu huyu samaki," alisema mama wa kambo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2197_13', 3, NULL, 'assets/topic/asb/1851.png', 'Mpunzi aliporejea jioni, alimpata mkewe mpya akiwa chumbani kwake akilia. "Unasumbuliwa na nini mke wangu mrembo?" Mpunzi aliuliza. "Mganga aliniambia kwamba njia pekee ya kukuzalia mtoto mvulana ni kumla samaki mkubwa aishiye kwenye mto ulio karibu hapa," alisema. "Je, utaniletea samaki huyo usiku wa leo?" mwanamke akamsihi. "Nitakufanyia lolote," Mpunzi akasema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2197_14', 3, NULL, 'assets/topic/asb/1852.png', 'Mpunzi alianza safari ya kwenda mtoni kabla giza kuingia. Aliwachukua wafanyakazi wake ili wamsaidie. Mpunzi alimpata samaki mkubwa zaidi. Alipofika nyumbani, moto mkubwa wa kumpika yule samaki ulikuwa tayari. "Nitatayarisha kitoweo kizuri," mwanamke akamwelezea Mpunzi aliyekuwa amefurahi sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2197_15', 3, NULL, 'assets/topic/asb/1853.png', 'Usiku ule Mpunzi na mkewe walienda kulala baada ya chakula kitamu. Waliacha mifupa mezani ili Nosisa asafishe asubuhi yake. Nosisa alipowaletea kahawa chumbani, walikuwa bado wanalala. "Hili ni jambo geni," Nosisa alifikiria. "Babangu huamka mapema mno kwenda kazini. Kwa nini leo bado analala?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2197_16', 3, NULL, 'assets/topic/asb/1854.png', '"Heri nikimbie mtoni nipate kiamsha kinywa kizuri," alifikiria. Nosisa alikimbia kwenda mtoni. Alipofika kule, alimwita Samaki mara nyingi lakini Samaki hakuja. Nosisa alianza kulia. Hakujua kilichotokea. Alipokuwa akilia, ndege mmoja alimwambia kilichotendeka. Alilia kwa nguvu akidhani kwamba mamake alikuwa ameangamizwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2197_17', 3, NULL, 'assets/topic/asb/1855.png', 'Ndege alimwambia aiokote mifupa kutoka mezani kisha airushe mtoni. Angefanya hivyo, Samaki angefufuka. Alimwambia pia awaache babake na mamake wa kambo walale hadi adhuhuri. Nosisa alifanya alivyoambiwa. Aliichukuwa mifupa, akaiweka mfukoni kisha akairusha mtoni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2197_18', 3, NULL, 'assets/topic/asb/1856.png', 'Nosisa alikimbia kurudi nyumbani na kabla adhuhuri kufika, aliwaamsha babake na mamake wa kambo. Hakuwatambua! Walikuwa wamezeeka na kudhoofika. Hawangemkaripia wala kumpiga tena. Walichoka wasiweze kuuchunga mji wao. Nosisa aliichukua mali ya babake. Akawaita wafanyakazi wote na kuwaambia kilichotokea. Wakasherehekea na kuishi kwa amani.', NULL);
