INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2974', 2, 'Sare za shule', 'assets/topic/asb/954.png', 'Gladys Njoki and Racheal Njoki
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2974_3', 3, NULL, 'assets/topic/asb/955.png', 'Rinda hili ni refu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2974_4', 3, NULL, 'assets/topic/asb/956.png', 'Sweta hii ni kubwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2974_5', 3, NULL, 'assets/topic/asb/957.png', 'Mkoba huu ni mkubwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2974_6', 3, NULL, 'assets/topic/asb/958.png', 'Mshipi huu ni mfupi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2974_7', 3, NULL, 'assets/topic/asb/959.png', 'Kofia hii ni ndogo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2974_8', 3, NULL, 'assets/topic/asb/960.png', 'Soksi hizi ni fupi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2974_9', 3, NULL, 'assets/topic/asb/961.png', 'Lakini, viatu hivi ni vipya.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2974_10', 3, NULL, 'assets/topic/asb/954.png', 'Vinanitosha vizuri.', NULL);
