INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2397', 2, 'Sayari Jua ya Saba', 'assets/topic/asb/2316.png', 'Kamundi Mutugi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2397_3', 3, NULL, 'assets/topic/asb/2317.png', 'Hapo zamani za kale kulikuwa na sayari jua saba angani. Miale mikali ya sayari jua hizo iling''aa duniani hadi binadamu wakashindwa kuvumilia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2397_4', 3, NULL, 'assets/topic/asb/2318.png', 'Ndugu saba wa kabila la Munda waliamua kuziua sayari jua hizo. Walizirushia mishale lakini wakaweza kuziua sayari jua sita pekee.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2397_5', 3, NULL, 'assets/topic/asb/2319.png', 'Sayari Jua ya saba ilikuwa imejificha nyuma ya mlima.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2397_6', 3, NULL, 'assets/topic/asb/2320.png', 'Baada ya sayari jua sita kuuawa, giza lilitanda kila mahali. Kwa sababu hiyo, paa hangeweza kumwona chui. Tembo walijikwaa kwenye miti na sungura wakatembea kwenye hatari ya simba. Wanyama wote walitaabika.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2397_7', 3, NULL, 'assets/topic/asb/2328.png', 'Wanyama wote walikutana kutatua shida hii. Sungura alisema kwamba Sayari Jua ya saba ilikuwa imejificha nyuma ya mlima. Wnayama walijiuliza, "Ni nani atakayeiita Sayari Jua hiyo irudi?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2397_8', 3, NULL, 'assets/topic/asb/2321.png', '"Nitaiita irudi," alisema Simba, mfalme wa msitu huo. "Jua, jua, tafadhali usitutoroke. Rudi utuangazie." Simba alinguruma. Lakini, Sayari Jua ilikataa kumsikiliza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2397_9', 3, NULL, 'assets/topic/asb/2322.png', 'Tembo naye akajaribu kuiita Sayari jua. Aliuinua mkonga wake na kupaaza sauti, "Jua, jua tafadhali rudi." Lakini, Sayari Jua ilikataa kumsikiliza pia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2397_10', 3, NULL, 'assets/topic/asb/2323.png', 'Mrembo Tausi alicheza densi kidogo na kisha akajaribu kuiita Sayari Jua, "Jua, jua, tafadhali rudi." Sayari jua ilikataa kujitokeza. Wanyama wote walijaribu kuiita Sayari Jua. Lakini Sayari Jua haikumsikiza yeyote. Jogoo alipojitokeza kuiita Sayari Jua, kila mmoja alimcheka. Simba, kama kiongozi mzuri,  alisema, "Tumpeni Jogoo nafasi ajaribu kuiita Sayari Jua."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2397_11', 3, NULL, 'assets/topic/asb/2324.png', 'Jogoo alisimama mbele ya wanyama wote, na taratibu akawika, "Kookoorikoo! Kookoorikoo!" Mara Sayari Jua ikaanza kuchomoza kutoka nyuma ya mlima. Wanyama wote walishangaa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2397_12', 3, NULL, 'assets/topic/asb/2325.png', 'Jogoo akawika tena kwa nguvu zaidi, "Kookoorikoo!" Sayari Jua ikachomoza hata zaidi. Jogoo akawika kwa mara ya tatu, "Kookoorikoo!" Sayari Jua ikapanda juu zaidi na kung''aa. Kukawa na mwangaza kila mahali. Wanyama wote na binadamu wakafurahi kuuona mwangaza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2397_13', 3, NULL, 'assets/topic/asb/2326.png', 'Wanyama waliwaomba binadamu wasiiue Sayari Jua. Tangu siku hiyo, jogoo huwika kila asubuhi. Nayo Sayari Jua isikiapo sauti ya jogoo, huchomoza na kuangaza dunia yote.', NULL);
