INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109', 2, 'Sherehe ya kuzaliwa kwa Lina asiyoitarajia', 'assets/topic/asb/18612.png', 'Ursula  Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109_3', 3, NULL, 'assets/topic/asb/18613.png', 'Lina, Opondo, na Anton walikuwa wakifanya matayarisho ya kusherehekea kuzaliwa kwa Lina.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109_4', 3, NULL, 'assets/topic/asb/18599.png', 'Anton na Lina walitaka kuoka keki. Opondo alianza kusoma kitabu cha mapishi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109_5', 3, NULL, 'assets/topic/asb/18600.png', '"Hebu tuvichanganye viungo hivi katika bakuli kubwa,"  Lina alisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109_6', 3, NULL, 'assets/topic/asb/18601.png', 'Anton alivikoroga viungo vyote akasababisha mchafuko mkubwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109_7', 3, NULL, 'assets/topic/asb/18602.png', '"Keki inaonekana kuwa nzito sasa," Opondo alisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109_8', 3, NULL, 'assets/topic/asb/18603.png', '"Marafiki zako wamewasili," Opondo alimjulisha Lina.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109_9', 3, NULL, 'assets/topic/asb/18604.png', 'Anton alidhani kwamba zawadi ya kwanza ingekuwa nzito kwa sababu ilikuwa kubwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109_10', 3, NULL, 'assets/topic/asb/18605.png', 'Ulikuwa mto na ulikuwa mwepesi sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109_11', 3, NULL, 'assets/topic/asb/18606.png', 'Anton tena alifikiri kuwa zawadi itakayofuata ingekuwa nyepesi kwa sababu ilikuwa ndogo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109_12', 3, NULL, 'assets/topic/asb/18607.png', 'Lilikuwa jiwe lililopakwa rangi na lilikuwa nzito sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109_13', 3, NULL, 'assets/topic/asb/18608.png', 'Watoto walisema kwamba kilo moja ya mawe ilikuwa na uzani sawa na kilo moja ya manyoya.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109_14', 3, NULL, 'assets/topic/asb/18609.png', 'Keki ya Lina iliungua.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109_15', 3, NULL, 'assets/topic/asb/18610.png', 'Lina alihuzunika sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109_16', 3, NULL, 'assets/topic/asb/18611.png', 'Shangazi Molly alimwuliza Anton, "Unadhani zawadi ya Lina itakuwa na uzito kiasi gani?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109_17', 3, NULL, 'assets/topic/asb/18598.png', 'Anton alimjibu, "Nadhani itakuwa na uzito sawa na ile keki iliyoungua."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109_18', 3, NULL, 'assets/topic/asb/18612.png', 'Kila mmoja alisema, "Lina, tunakutakia siku ya kuzaliwa yenye furaha."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23109_19', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707', 2, 'Siafu amwokoa Njiwa', 'assets/topic/asb/788.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_3', 3, NULL, 'assets/topic/asb/784.png', 'Ilikuwa siku yenye joto jingi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_4', 3, NULL, 'assets/topic/asb/785.png', 'Siafu Mdogo hakuwa amepata maji kwa siku nyingi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_5', 3, NULL, 'assets/topic/asb/786.png', 'Akawaza, "Ninahitaji tone moja tu la maji, hata kama litatoka kwenye jani."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_6', 3, NULL, 'assets/topic/asb/787.png', 'Umande wote ulikuwa umekauka. Hangepata hata tone moja la moja.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_7', 3, NULL, 'assets/topic/asb/789.png', 'Siafu Mdogo akalia sana, "Nisipokunywa maji,  nitakufa. Lazima niufikie mto nilioelezwa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_8', 3, NULL, 'assets/topic/asb/790.png', '"Ati mtoni? Huko utakufa maji!" Kichakuro mmoja mwenye hekima alimwonya.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_9', 3, NULL, 'assets/topic/asb/791.png', 'Lakini, Siafu Mdogo hakujali. Kiu chake kiliongezeka. "Nitakufa iwapo sitakunywa maji sasa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_10', 3, NULL, 'assets/topic/asb/870.png', 'Siafu Mdogo alienda kuutafuta mto alioambiwa juu yake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_11', 3, NULL, 'assets/topic/asb/871.png', 'Alipitia kwenye nyasi na matawi yaliyokauka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_12', 3, NULL, 'assets/topic/asb/872.png', 'Mara akasikia mngurumo wa mawimbi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_13', 3, NULL, 'assets/topic/asb/873.png', 'Siafu Mdogo alifika mtoni akayanywa maji baridi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_14', 3, NULL, 'assets/topic/asb/874.png', 'Alifurahi kukata kiu na hakuliona wimbi kubwa likija.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_15', 3, NULL, 'assets/topic/asb/875.png', 'Siafu Mdogo alijaribu kujishikilia kwenye nyasi kavu zilizokuwa zikielea. Lakini, alisombwa na maji.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_16', 3, NULL, 'assets/topic/asb/876.png', 'Siafu Mdogo alilia, "Jamani, nisaidieni."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_17', 3, NULL, 'assets/topic/asb/877.png', 'Njiwa Mweupe alikuja akamnyooshea tawi. Akasema,  "Panda juu ya tawi haraka."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_18', 3, NULL, 'assets/topic/asb/878.png', 'Siafu Mdogo aliruka juu ya tawi na Njiwa Mweupe akamwokoa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_19', 3, NULL, 'assets/topic/asb/879.png', 'Siafu Mdogo akasema, "Siondoki kabla sijamshukuru Njiwa. Nitasubiri hadi atakaporejea kunywa maji."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_20', 3, NULL, 'assets/topic/asb/880.png', 'Siku moja alipokuwa akimsubiri Njiwa Mweupe, Jona na Petro walikuja mtoni. Walikuwa wamebeba manati.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_21', 3, NULL, 'assets/topic/asb/881.png', '"Yule Njiwa Mweupe ambaye huja hapa kunywa maji,  tutamla jioni." Jona akawaza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_22', 3, NULL, 'assets/topic/asb/882.png', 'Jona na Petro walijificha nyuma ya mimea kando ya mto.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_23', 3, NULL, 'assets/topic/asb/883.png', '"Sitakubali wajanja hao wamuue yule Njiwa Mweupe. Lakini nitafanyaje? Mimi ni mdogo sana." Siafu aliwaza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_24', 3, NULL, 'assets/topic/asb/884.png', 'Wakati huo huo Njiwa Mweupe aliruka kutoka mtini anywe maji.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8707_25', 3, NULL, 'assets/topic/asb/885.png', 'Jona na Petro walipima shabaha na kungoja kwa hamu.', NULL);
