INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15615_4', 3, NULL, 'assets/topic/asb/14598.png', 'Mama Jua alipoondoka,  Milima ilihuzunika.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15615_5', 3, NULL, 'assets/topic/asb/14600.png', 'Upepo uliitikisa Miti.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15615_6', 3, NULL, 'assets/topic/asb/14602.png', 'Anga lilianza kulalamika.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15615_7', 3, NULL, 'assets/topic/asb/14604.png', 'Mawingu yalihuzunika.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15615_8', 3, NULL, 'assets/topic/asb/14605.png', 'Ulimwengu mzima ukaanza kuzama majini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15615_9', 3, NULL, 'assets/topic/asb/14606.png', 'Wakati huo, Mama Jua alimbusu dadake Mwezi.
