INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15032', 2, 'Siku niliyokwenda Jijini', 'assets/topic/asb/15008.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15032_3', 3, NULL, 'assets/topic/asb/14993.png', 'Kituo cha basi kijijini kwetu kilikuwa na shughuli nyingi. Basi zilijaza mizigo huku abiria wakizitafuta basi walizotaka kuabiri. Kondakta waliwaelekeza abiria kwenye basi zilizokwenda sehemu mbalimbali.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15032_4', 3, NULL, 'assets/topic/asb/14994.png', 'Nilimsikia kondakta mmoja akiita kwa sauti, "Jiji! Jiji! Wanaokwenda magharibi!" Lile ndilo basi nililohitaji kuabiri kwa usafiri wangu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15032_5', 3, NULL, 'assets/topic/asb/14995.png', 'Basi la kwenda jijini lilikuwa karibu kujaa. Hata hivyo, watu zaidi walitaka kuingia. Baadhi yao walipakia mizigo chini ya basi. Wengine waliiweka ndani kwenye rafu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15032_6', 3, NULL, 'assets/topic/asb/14997.png', 'Baadhi ya abiria walishika tiketi zao huku wakitafuta viti vilivyokuwa wazi. Wanawake waliokuwa na watoto wachanga waliwatayarisha kwa safari hiyo itakayokuwa ndefu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15032_7', 3, NULL, 'assets/topic/asb/14996.png', 'Nilipenyeza ndani nikapata kiti karibu na dirisha. Abiria aliyeketi karibu nami alishika karatasi ya plastiki ya kijani kibichi. Alivaa viatu vilivyozeeka na koti kukuu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15032_8', 3, NULL, 'assets/topic/asb/14998.png', 'Nilitazama nje nikawaza, "Ninaondoka hapa mahali nilipolelewa nikienda kwenye jiji kubwa. Sijui ikiwa nitawahi kurudi hapa tena."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15032_9', 3, NULL, 'assets/topic/asb/14999.png', 'Upakiaji mizigo ulikamilika na abiria wakawa wameketi. Wachuuzi walizidi kusukumana wakitaka kuuza bidhaa zao. Walitaja majina ya bidhaa walizouza kwa sauti ya juu. Maneno yao yalinifurahisha.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15032_10', 3, NULL, 'assets/topic/asb/15000.png', 'Baadhi ya abiria walinunua vinywaji. Wengine walinunua vitavunaji vidogo na kutavuna. Tusiokuwa na fedha, tulitazama tu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15032_11', 3, NULL, 'assets/topic/asb/15001.png', 'Shughuli hizo zilikatizwa kwa mlio wa honi ya basi. Tulikuwa tayari kuondoka. Kondakta aliwataka wachuuzi wote washuke kutoka basini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15032_12', 3, NULL, 'assets/topic/asb/15002.png', 'Wachuuzi walisukumana kutoka nje. Wachache waliwarudisha abiria masalio ya hela zao. Wengine walifanya juhdi za mwisho kuuza bidhaa zao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15032_13', 3, NULL, 'assets/topic/asb/15003.png', 'Basi lilipoondoka kituoni, nilichungulia dirishani. Nilikitazama kituo hicho kilichojaa magari nikiwaza,  "Pengine hii itakuwa mara yangu ya mwisho kukiona kituo hiki."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15032_14', 3, NULL, 'assets/topic/asb/15004.png', 'Safari ilipoendelea, nilihisi joto jingi. Niliyafumba macho nikinuia kulala.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15032_15', 3, NULL, 'assets/topic/asb/15005.png', 'Nilijiuliza maswali mengi. "Je, mamangu atakuwa salama? Je, sungura wangu watatufaidi? Je, ndugu yangu atakumbuka kunyunyizia maji miche ya miti yangu?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15032_16', 3, NULL, 'assets/topic/asb/15006.png', 'Njiani, nilikariri jina la mahali mjombangu aliishi kule jijini. Hatimaye, nilipatwa na usingizi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15032_17', 3, NULL, 'assets/topic/asb/15007.png', 'Baada ya saa tisa, niliamshwa kwa kelele nyingi. Kondakta alikuwa akiwaita abiria waliotaka kwenda kijijini kwetu. Nilichukua mkoba wangu mdogo nikaruka nje.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15032_18', 3, NULL, 'assets/topic/asb/15008.png', 'Basi hilo lililokuwa likirudi lilikuwa linajaa upesi. Muda mfupi baadaye, lingeanza safari ya kwenda mashariki. Jambo la mhimu kwangu, lilikuwa kuanza kutafuta nyumba ya mjombangu.', NULL);
