INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8433', 2, 'Siku niliyopotea sokoni', 'assets/topic/asb/1447.png', 'Pete Mhunzi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8433_3', 3, NULL, 'assets/topic/asb/1448.png', 'Mimi ninaishi katika kijiji cha Kakuma. Ni mahali pakavu penye miti ya miiba na joto jingi. Kakuma,  kuna mbuzi wengi. Wenyeji wengi hupanda baiskeli. Sokoni kuna maduka machache madogo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8433_4', 3, NULL, 'assets/topic/asb/1449.png', 'Asubuhi moja, mamangu aliniita akasema, "Etabo,  leo umefika umri wa miaka sita nasi tutakupa zawadi." Nilimuuliza nikiwa na furaha tele, "Zawadi gani? Zawadi gani?" Akajibu, "Utafuatana nasi kesho twende sokoni. Tutapanda basi." Hiyo ingekuwa safari yangu ya kwanza kwenda soko kubwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8433_5', 3, NULL, 'assets/topic/asb/1450.png', 'Keshoye, tulikwenda kwenye kituo cha basi. Nilisimama kati ya mama na shangazi. Nilijisikia mdogo sana nikiwa kati yao. Nilikuwa nimevaa suruali mpya ya bluu na fulana nyekundu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8433_6', 3, NULL, 'assets/topic/asb/1451.png', 'Basi lilikuwa limejaa kwa hivyo ilibidi mama anipakate. Nililala njia nzima Kwa vile nilikuwa nimechoka sana kutokana na joto. Njiani sikuona chochote.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8433_7', 3, NULL, 'assets/topic/asb/1452.png', 'Jua lilikuwa limeanza kuchomoza tulipofika sokoni. Langoni, palikuwa na mama muuzaji wa nafaka. Pembeni, walikuwapo wanaume wawili waliokuwa wakiviandaa viazi vitamu. Upande wa pili, mama mmoja mfupi alishika helikopta ya rangi ya bluu iliyometameta. Nilipoina, niliita, "Mama, mama,  tazama helikopta ile!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8433_8', 3, NULL, 'assets/topic/asb/1453.png', 'Katikati ya soko, kulikuwa na kibanda kikubwa. Matunda aina nyingi yaliuzwa pale. "Yanaitwaje matunda haya?" Nilimwuliza mama huku nikimwonyesha aina ambayo sikuwahi kuiona.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8433_9', 3, NULL, 'assets/topic/asb/1454.png', 'Niliendelea kuuliza, "Na haya je?" Mama akajibu,  "Haya ni matofaa." Kati ya matunda yote kibandani,  nilipenda matofaa zaidi. Nilipotazama umbo na rangi yake, niliwazia utamu wake. "Tafadhali mama ninunulie tofaa moja?" Niliuliza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8433_10', 3, NULL, 'assets/topic/asb/1455.png', 'Pindi nilipopewa tofaa, niliuachilia mkono wa mama,  nikalishika tunda kwa mikono miwili. Nililitafuna polepole nikaonja utamu wake. Nilikuwa sijawahi kulionja tunda tamu kama tofaa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8433_11', 3, NULL, 'assets/topic/asb/1456.png', 'Nilipomaliza kulila tofaa, niligeuka kuongea na mama, lakini hakuwepo! Nilimtafuta mahali tulipotoka, lakini sikumwona yeye wala shangazi. Nikamwuliza mama muuzaji nafaka, lakini hakujua walipokuwa. Nikaanza kulia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8433_12', 3, NULL, 'assets/topic/asb/1457.png', 'Baadaye, mama mmoja alinishika mkono na kuniongoza walipokuwa watoto wengine. Mzee mmoja mwenye ndevu aliniuliza, "Jina lako nani mwanangu?" Huku nikilia, nilimjibu, "E-ta-bo."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8433_13', 3, NULL, 'assets/topic/asb/1458.png', 'Nilianza kujiuliza kama watoto pia huuzwa sokoni. Mara nikaacha kulia ili nione kama wanunuzi wa watoto walikuwepo chumbani mle. Baada ya muda alikuja mama mmoja akamchagua mmojawapo wa watoto. Nikawaza, "Mimi nikichaguliwa, sitarudi nyumbani tena?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8433_14', 3, NULL, 'assets/topic/asb/1459.png', 'Kisha nilimsikia yule mwanamume mwenye ndevu akiuliza, "Etabo yuko wapi?" Nililia zaidi nikisema, "Lakini mimi sitaki kwenda na wewe!" Nikatoroka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8433_15', 3, NULL, 'assets/topic/asb/1460.png', 'Walipolisikia jina langu likiitwa, mama na shangazi walikimbilia chumbani humo. "Etabo, Etabo." Iliita sauti niliyoitambua kuwa ya mamangu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8433_16', 3, NULL, 'assets/topic/asb/1460.png', 'Nilimkumbatia mamangu. Kisha shangazi akasema,  "Etabo, tulikuwa tunakutafuta tukupe zawadi yako ya siku ya kuzaliwa." Aliitoa helikpta iliyometameta ya rangi ya bluu kutoka kwenye mfuko mkubwa alioubeba. "Hii hapa zawadi yako!" Akasema mama.', NULL);
