INSERT INTO `card` (id, type, title, header, content, option) VALUES ('11139', 2, 'Siku tuliyouona upinde', 'assets/topic/asb/10970.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('11139_3', 3, NULL, 'assets/topic/asb/10965.png', 'Jina langu ni Kobole. Ninaishi katika kijiji kiitwacho Mbalisana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('11139_4', 3, NULL, 'assets/topic/asb/10966.png', 'Siku moja nilikuwa nikichuma matunda. Nilishuhudia jambo la kushangaza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('11139_5', 3, NULL, 'assets/topic/asb/10967.png', 'Niliona mistari ya rangi tofauti angani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('11139_6', 3, NULL, 'assets/topic/asb/10969.png', 'Niliyaangusha matunda niliyokuwa nimechuma. Kisha, nikakimbia kuwaambia watu nyumbani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('11139_7', 3, NULL, 'assets/topic/asb/10970.png', 'Niliwapata wakiwa wamesimama nje wakitazama juu kwa mshangao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('11139_8', 3, NULL, 'assets/topic/asb/10971.png', 'Nilisema kwa furaha, "Hii ni shuka maridadi." Neno alinipinga akisema, "La! Hii ni rangi tofauti zinamwagika kutoka angani."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('11139_9', 3, NULL, 'assets/topic/asb/10973.png', 'Baadhi ya wanakijiji walikubaliana nami na wengine waliamini aliyosema Neno. Tulianza kubishana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('11139_10', 3, NULL, 'assets/topic/asb/10975.png', 'Wakati huo, mgeni mmoja alitokea na kusema,  "Mmekosea nyote."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('11139_11', 3, NULL, 'assets/topic/asb/10976.png', 'Tulipiga mayowe, "Ikiwa tumekosea, wewe tueleze ukweli."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('11139_12', 3, NULL, 'assets/topic/asb/10977.png', 'Yule mgeni alisema, "Mnachoshuhudia ni upinde. Kwani ninyi hamjawahi kuuona upinde?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('11139_13', 3, NULL, 'assets/topic/asb/10979.png', 'Kwa mshangao zaidi, tulimjibu, "La! U-p-i-n-d-e ni nini?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('11139_14', 3, NULL, 'assets/topic/asb/10980.png', 'Neno alimwuliza, "Je, huu upinde ni wetu? Tunaweza kuuweka hapa?" Hatukupata majibu kwa maswali yetu. Mgeni huyo alikuwa tayari ameenda zake.', NULL);
