INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21059', 2, 'Simba Jike na Mbuni', 'assets/topic/asb/19205.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21059_3', 3, NULL, 'assets/topic/asb/19195.png', 'Mbuni na Simba Jike walikuwa marafiki wakati mmoja.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21059_4', 3, NULL, 'assets/topic/asb/19196.png', 'Mbuni aliwalisha wanawe vyema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21059_5', 3, NULL, 'assets/topic/asb/19197.png', 'Watoto wa Simba Jike walikonda.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21059_6', 3, NULL, 'assets/topic/asb/19198.png', 'Simba Jike alitamani vifaranga wa Mbuni kuwa wake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21059_7', 3, NULL, 'assets/topic/asb/19199.png', 'Siku moja, Mbuni alikwenda safari.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21059_8', 3, NULL, 'assets/topic/asb/19200.png', 'Mbuni aliporudi, aliuliza, "Watoto wangu wako wapi?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21059_9', 3, NULL, 'assets/topic/asb/19201.png', 'Mbuni alimkuta Simba Jike na watoto wake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21059_10', 3, NULL, 'assets/topic/asb/19202.png', 'Simba Jike alimwambia, "Vifaranga ni wangu sasa. Wachukue wana simba."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21059_11', 3, NULL, 'assets/topic/asb/19203.png', 'Mbuni alijiuliza, "Nitawapataje wanangu?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21059_12', 3, NULL, 'assets/topic/asb/19204.png', 'Mbuni aliitisha mkutana wa wanyama wote.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21059_13', 3, NULL, 'assets/topic/asb/19205.png', 'Wanyama walifika kuhudhuria mkutano.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21059_14', 3, NULL, 'assets/topic/asb/19206.png', 'Tembo aliwauliza Mbuni na Simba Jike kujieleza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21059_15', 3, NULL, 'assets/topic/asb/19207.png', 'Wanyama wengi waliogopa kumulaumu Simba Jike.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21059_16', 3, NULL, 'assets/topic/asb/19208.png', 'Kichakuro alisema, "Watoto wanaofanana na ndege, ni wa Mbuni."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21059_17', 3, NULL, 'assets/topic/asb/19209.png', 'Baada ya kusema hivyo, Kichakuro alitoroka akaingia shimoni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21059_18', 3, NULL, 'assets/topic/asb/19210.png', 'Simba Jike alikasirika sana.
