INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22002_4', 3, NULL, 'assets/topic/asb/1676.png', 'Siku moja, baba yake Simbi alikwenda nyumbani na mwanamke.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22002_5', 3, NULL, 'assets/topic/asb/1677.png', 'Baba yake alimtaka Simbi kumkubali Anita kama mama yake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22002_6', 3, NULL, 'assets/topic/asb/1678.png', 'Simbi hakufurahi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22002_7', 3, NULL, 'assets/topic/asb/1679.png', 'Baba yake Simbi alienda kufanya kazi mbali.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22002_8', 3, NULL, 'assets/topic/asb/1680.png', 'Anita alizoea kumpiga Simbi.
