INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8438', 2, 'Sungura chini ya mti', 'assets/topic/asb/7610.png', 'Pete Mhunzi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8438_3', 3, NULL, 'assets/topic/asb/7595.png', 'Sungura alikuwa amelala chini ya mtofaa. Tofaa lilianguka chini kutoka tawi moja.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8438_4', 3, NULL, 'assets/topic/asb/7596.png', 'Sungura aliisikia sauti ikisema, ''Kimbia, Sungura, kimbia!'' Sungura aliamka na bila kukawia akaanza kukimbia mbio sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8438_5', 3, NULL, 'assets/topic/asb/7597.png', 'Sungura alikutana na Kuku. "Mbona unakimbia?" Kuku alimwuliza. Sungura akamjibu, "Sijui. Nilisikia tu kitu kikianguka nayo sauti ikasema, ''Kimbia, Sungura kimbia!''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8438_6', 3, NULL, 'assets/topic/asb/7598.png', 'Kuku alishituka akaanza kukimbia alipomsikia maneno aliyosema Sungura.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8438_7', 3, NULL, 'assets/topic/asb/7599.png', 'Walikutana na Mbwa akawauliza, "Kwa nini mnakimbia?" Kuku akamjibu, "Sijui. Nilisikia Sungura akipiga makelele naye hajui. Alisikia kitu kikianguka na sauti ikisema, ''Kimbia, Sungura kimbia!''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8438_8', 3, NULL, 'assets/topic/asb/7600.png', 'Mbwa akashangaa akaanza kukimbia pamoja na Sungura na Kuku.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8438_9', 3, NULL, 'assets/topic/asb/7601.png', 'Walikutana na Farasi akawamwuliza Mbwa, "Mbona mnakimbia?" Mbwa akamjibu, "Sijui. Nilisikia Kuku akisema hajui na wala Sungura hajui. Alisikia kitu kikianguka na sauti ikisema, ''Kimbia, Sungura kimbia!''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8438_10', 3, NULL, 'assets/topic/asb/7602.png', 'Farasi naye akaanza kukimbia pamoja na Sungura, Mbwa na Kuku.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8438_11', 3, NULL, 'assets/topic/asb/7603.png', 'Walikutana na Punda akawauliza, "Kwa nini mnakimbia?" Farasi alimjibu, "Sijui. Nilimsikia Mbwa akisema hajui. Yeye alimsikia Kuku ambaye hajui. Kuku naye alimsikia Sungura ambaye pia hajui. Alisikia tu kitu kikianguka na sauti ikisema, ''Kimbia, Sungura, kimbia!''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8438_12', 3, NULL, 'assets/topic/asb/7604.png', 'Basi Punda pia akaanza kukimbia pamoja na Sungura, Mbwa, Kuku na Farasi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8438_13', 3, NULL, 'assets/topic/asb/7605.png', 'Walikutana na Ng''ombe akawauliza, "Kwa nini mnakimbia?" Punda alimjibu, "Sijui. Nilisikia alivyosema Farasi, naye hajui. Yeye alisikia Mbwa na Mbwa hajui. Alimsikia Kuku naye Kuku hajui. Alimsikia Sungura naye pia hajui. Yeye alisikia kitu kikianguka na sauti ikisema, ''Kimbia, Sungura, kimbia!''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8438_14', 3, NULL, 'assets/topic/asb/7606.png', 'Ng''ombe akapata wasiwasi na akaanza kukimbia pamoja na Sungura, Mbwa, Kuku, Farasi na Punda.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8438_15', 3, NULL, 'assets/topic/asb/7607.png', 'Walikutana na Paka akauliza, "Kwa nini mnakimbia?" Ng''ombe alijibu, "Sijui. Nilisikia tu alivyosema Punda naye hajui. Yeye alimsikia Farasi naye Farasi hajui. Alimsikia Mbwa ambaye hajui. Yeye alimsikia Kuku na Kuku hajui. Kuku alimsikia Sungura ambaye hajui ila alisikia kitu kikianguka na sauti ikisema, ''Kimbia, Sungura, kimbia!''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8438_16', 3, NULL, 'assets/topic/asb/7608.png', 'Paka naye akaanza kukimbia pamoja na wanyama wengine.', NULL);
