INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3083_3', 3, NULL, 'assets/topic/asb/2037.png', 'Je,  unakumbuka Mbio Kuu zilizokuwa kati ya Sungura na Kobe? Kwa muda mrefu, hakuna yeyote katika miliki ya wanyama aliyeweza kuongea juu ya jambo tofauti ila zile Mbio Kuu na wapinzani wake wawili.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3083_5', 3, NULL, 'assets/topic/asb/2039.png', 'Inajulikana kwamba Sungura alishindwa kwa kuwa mvivu na mwenye kujiamini zaidi. Vilevile inajulikana kuwa Kobe alishinda kwa mwendo wake wa polepole na kwa kuwa imara. Wanyama wa pori walijua hili na wakaendelea kuwaheshimu wanyama hao wawili kwa kiwango sawa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3083_6', 3, NULL, 'assets/topic/asb/2040.png', 'Kobe na Sungura waliendelea kuwa marafiki. Kobe hakujivunia ushindi wake, na Sungura, akifahamu kuwa alikuwa ameshindwa kwa uwazi, hakumshikia Kobe kisasi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3083_7', 3, NULL, 'assets/topic/asb/2042.png', 'Miezi mingi ilipita. Mfalme wa pori ambako Sungura na Kobe waliishi alikuwa na jambo muhimu alilotaka kujadiliana na mfalme wa pori jirani. Lakini mfalme wa kwanza hangeweza kuondoka pale wakati huo. Badala yake, aliwatuma Sungura na Kobe kama wawakilishi kwa yule mfalme mwingine.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3083_8', 3, NULL, 'assets/topic/asb/2043.png', '"Mmoja wenu angalau, atalazimika kwenda kwa ufalme jirani," aliamrisha mfalme wakati Sungura na Kobe walikuja mbele zake. "Ninataka mjadili mambo muhimu na mfalme wa huko, halafu mniletee ripoti kuhusu maoni na mawazo yake juu ya mambo hayo. Sasa, nendeni!" Alisema alipomaliza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3083_9', 3, NULL, 'assets/topic/asb/2044.png', 'Walipokuwa wakiondoka, aliongeza, "Kumbukeni,  mna siku moja tu ya kukamilisha kazi hiyo."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3083_10', 3, NULL, 'assets/topic/asb/2045.png', 'Barabara ya kwenda kwa ufalme jirani haikuwa laini wala rahisi. Ilijaa miiba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3083_11', 3, NULL, 'assets/topic/asb/2046.png', 'Vilevile, walilazimika kuvuka mito miwili iliyokuwa na mawe makubwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3083_12', 3, NULL, 'assets/topic/asb/2048.png', 'Baada ya kuwaza kiasi, Sungura na Kobe waligundua kwamba hakuna mmoja wao ambaye angeweza kuitimiza ile kazi peke yake. Walilazimika kusafiri pamoja. Mpango ulikuwa kwamba Sungura ambebe Kobe watakapokuwa wakipita sehemu yenye miiba. Naye Kobe ambebe Sungura wakati watakapoavuka mito.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3083_13', 3, NULL, 'assets/topic/asb/2050.png', 'Siku iliyofuata, walikusanya jumbe tofauti kutoka kwa mfalme wao na kujitayarisha kuondoka. Sungura alitembea kwa hatua refu refu akakamilisha mwendo wa kupita sehemu ya miiba kwa kasi. Kobe alijishikilia asije akaanguka na kupoteza maisha yake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3083_14', 3, NULL, 'assets/topic/asb/2051.png', 'Walipofikia mto, walibadilishana na Sungura akampanda Kobe mgongoni. Kobe aliogelea kutoka upande mmoja wa mto hadi mwingine kwa wepesi. Baada ya kuvuka mito yote miwili, haikuwachukua muda mrefu kufika katika ule ufalme jirani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3083_16', 3, NULL, 'assets/topic/asb/2054.png', 'Baada ya kujadiliana na yule mfalme jirani kwa urefu mambo yote kutoka kwa mfalme wao, Sungura na Kobe walikuwa tayari kuondoka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3083_17', 3, NULL, 'assets/topic/asb/2056.png', 'Safari ya kurudi ilikuwa nyororo na nyepesi kuliko ya kwenda kwani wote wawili walijua la kufanya. Sungura na Kobe walikuwa wamefanya kazi pamoja kwa maelewano makubwa hata wakamfikia mfalme wao mapema!', NULL);
