INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13092', 2, 'Sungura na Tembo (Tena!)', 'assets/topic/asb/13000.png', 'Agnes Gichaba
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13092_3', 3, NULL, 'assets/topic/asb/13000.png', 'Hapo zamani za kale, Tembo na Sungura walikuwa marafiki wakubwa sana. Walikuwa na ngamia,  ng''ombe na kondoo wengi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13092_4', 3, NULL, 'assets/topic/asb/13001.png', 'Waliwalisha wanyama hao katika bonde la Kingilo. Wakati mwingine waliwapeleka milimani kulikokuwa na nyasi na maji kwa wingi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13092_5', 3, NULL, 'assets/topic/asb/13002.png', 'Wanyama walipokula, Sungura na Tembo walicheza. Walipenda sana kucheza mpira wa miguu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13092_6', 3, NULL, 'assets/topic/asb/13003.png', 'Kila walipocheza, Tembo aliingiza mabao mengi kumliko Sungura. Sungura alikasirishwa mno na hali hiyo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13092_7', 3, NULL, 'assets/topic/asb/13004.png', 'Siku moja Sungura alimwuliza Tembo, "Ni nini kinachokuwezesha kuwa mwingizaji goli maarufu?" Tembo alimjibu, "Ni miguu yangu minono."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13092_8', 3, NULL, 'assets/topic/asb/13005.png', 'Siku iliyofuata, Sungura alipendekeza wawe na mashindano ya mbio. Tembo alikubali na wakaanza mbio.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13092_9', 3, NULL, 'assets/topic/asb/13006.png', 'Sungura alishinda mbio zote. Tembo alikasirika. Hakuwahi kushindwa. Alimwuliza Sungura, "Ni nini kinachokuwezesha kuwa mkimbiaji maarufu?"  Sungura alijibu, "Ni miguu yangu myembamba."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13092_10', 3, NULL, 'assets/topic/asb/13007.png', 'Tembo alimwuliza, "Nitawezaje kupata miguu myembamba?" Sungura akamjibu, "Ni rahisi,  nitakuonyesha. Tafuta kuni  ukakoke moto."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13092_11', 3, NULL, 'assets/topic/asb/13008.png', 'Tembo alizikusanya kuni akakoka moto mkubwa. Sungura alimwambia, "Sasa iweke miguu yako ndani ya moto." Tembo alifanya hivyo. Alipiga kelele,  "Ninaungua! Ninaungua!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13092_12', 3, NULL, 'assets/topic/asb/13009.png', 'Sungura alifurahi. Alimhimiza Tembo aendelea hivyo hivyo. Alisema kuwa hivyo tu ndivyo miguu yake ingekuwa myembamba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13092_13', 3, NULL, 'assets/topic/asb/13010.png', 'Miguu ya Tembo ilijeruhiwa vibaya akaondoka motoni. Hakuweza kusimama kwa hivyo alilala chini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13092_14', 3, NULL, 'assets/topic/asb/13012.png', 'Tembo aliketi kwa siku nyingi kabla kuweza kusimama.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13092_15', 3, NULL, 'assets/topic/asb/13013.png', 'Hatimaye, aliweza kuchechemea na kwenda nyumbani kwa familia yake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13092_16', 3, NULL, 'assets/topic/asb/13014.png', 'Sungura alimwomba Tembo msamaha. Hata hivyo, urafiki kati ya Tembo na Sungura uliisha.', NULL);
