INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16197', 2, 'Taja Rangi', 'assets/topic/asb/1926.png', 'Mutugi Kamundi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16197_3', 3, NULL, 'assets/topic/asb/1242.png', 'Kwa nini mbingu ni ya rangi ya bluu?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16197_4', 3, NULL, 'assets/topic/asb/1243.png', 'Je,  huwa bluu kila wakati?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16197_5', 3, NULL, 'assets/topic/asb/1917.png', 'Kwa nini rangi ya majani ni kijani kibichi?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16197_6', 3, NULL, 'assets/topic/asb/1054.png', 'Majani si kijani kibichi kila wakati.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16197_7', 3, NULL, 'assets/topic/asb/1918.png', 'Kwa nini ndizi mbivu ni manjano?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16197_8', 3, NULL, 'assets/topic/asb/1919.png', 'Ndizi mbichi ni za kijani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16197_9', 3, NULL, 'assets/topic/asb/1920.png', 'Kwa nini mwavuli ni mweusi?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16197_10', 3, NULL, 'assets/topic/asb/1922.png', 'Miavuli yote si mieusi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16197_11', 3, NULL, 'assets/topic/asb/1923.png', 'Kwa nini damu ni nyekundu?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16197_12', 3, NULL, 'assets/topic/asb/1924.png', 'Kuna madini kwenye damu. Yanaipatia damu rangi  nyekundu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16197_13', 3, NULL, 'assets/topic/asb/1925.png', 'Kwa nini chumvi ni nyeupe?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16197_14', 3, NULL, 'assets/topic/asb/1928.png', 'Sifahamu!', NULL);
