INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21631', 2, 'Tamara aanza kwenda shule', 'assets/topic/asb/25447.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21631_3', 3, NULL, 'assets/topic/asb/25447.png', 'Tamara alikuwa msichana wa miaka mitano. Aliishi na familia yake. Kijiji chao kilikuwa karibu na msitu wenye wanyama wakali.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21631_4', 3, NULL, 'assets/topic/asb/25448.png', 'Tamara aliwatii wazazi wake. Aliwasaidia kuwachunga kondoo na mbuzi wao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21631_5', 3, NULL, 'assets/topic/asb/25449.png', 'Tamara alipenda kuwasikiliza ndege wakiimba. Ndege walipoimba, aliwaiga huku akitunga nyimbo zake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21631_6', 3, NULL, 'assets/topic/asb/25450.png', 'Kwa sababu ya umri wake mdogo, Tamara hakuwa ameanza kwenda shule. Nashipae na Tasieku walikwenda kwa sababu walikuwa wakubwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21631_7', 3, NULL, 'assets/topic/asb/25451.png', 'Nashipae, Tasieku na watoto wengine kutoka kijijini walienda pamoja asubuhi na kurudi jioni. Walikuwa wakibeba chakula katika mikebe midogo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21631_8', 3, NULL, 'assets/topic/asb/25452.png', 'Siku moja Tamara alimwuliza mamake, "Je, nitaanza kwenda shule lini?" Mama alimjibu, "Utaenda hivi karibuni mwanangu mpendwa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21631_9', 3, NULL, 'assets/topic/asb/25453.png', 'Siku moja, chifu na wanawake wawili walifika nyumbani kwa gari. Lilikuwa jukumu la chifu kuhakikisha kwamba watoto wanaanza kwenda shule walipofikisha umri uliokubaliwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21631_10', 3, NULL, 'assets/topic/asb/25454.png', 'Watu waliamini kwamba mtoto akiweza kuunyoosha mkono wake kupita kichwani na kulishika sikio lake, mtoto huyo yuko tayari kuanza kwenda shule.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21631_11', 3, NULL, 'assets/topic/asb/25455.png', 'Chifu alimwuliza Tamara, "Una umri wa miaka mingapi?" Tamara alimjibu, "Mitano." "Hebu tuone ikiwa unaweza kwenda shule," chifu alisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21631_12', 3, NULL, 'assets/topic/asb/25456.png', 'Mmoja wa wanawake wale aliuchukuwa mkono wa kulia wa Tamara akauweka kichwani kwake. Tamara aliweza kulishika sikio lake la kushoto.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21631_13', 3, NULL, 'assets/topic/asb/25457.png', 'Waliamua kwamba wakati ulikuwa umefika wa Tamara kuanza kuhudhuria shule. Angeanza kwenda shule iliyokuwa karibu na nyumbani kwao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21631_14', 3, NULL, 'assets/topic/asb/25458.png', 'Tamara alifurahi sana akawaza, "Nitajua kusoma na kuandika kama Nashipae na Tasieku wafanyavyo."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21631_15', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22003', 2, 'Tarik na rafiki yake', 'assets/topic/asb/25050.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22003_3', 3, NULL, 'assets/topic/asb/25047.png', 'Tarik na familia yake waliishi mbali na mji mkuu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22003_4', 3, NULL, 'assets/topic/asb/25048.png', 'Tarik alikuwa na mbwa aliyeitwa Buchi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22003_5', 3, NULL, 'assets/topic/asb/25049.png', 'Siku moja, Tarik na rafiki yake Bedilu waliwapeleka mifugo malishoni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22003_6', 3, NULL, 'assets/topic/asb/25050.png', 'Bedilu alipenda wacheze, lakini Tarik alitaka kusoma.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22003_7', 3, NULL, 'assets/topic/asb/25051.png', 'Mwishowe, Bedilu na Tarik waliamua kucheza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22003_8', 3, NULL, 'assets/topic/asb/25052.png', 'Walipokuwa wakicheza, kondoo walitoweka.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22003_9', 3, NULL, 'assets/topic/asb/25053.png', 'Mbwa mwitu aliua kondoo wa Tarik.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22003_10', 3, NULL, 'assets/topic/asb/25054.png', 'Tarik aliamua kulala juu ya mti.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22003_11', 3, NULL, 'assets/topic/asb/25055.png', 'Usiku, fisi walipiga kelele Tarik akaogopa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22003_12', 3, NULL, 'assets/topic/asb/25056.png', 'Tarik alianguka kutoka mtini.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22003_13', 3, NULL, 'assets/topic/asb/25057.png', 'Wakati huo, radi ilimlika anga.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22003_14', 3, NULL, 'assets/topic/asb/25058.png', 'Tarik na Buchi waliketi juu ya mti hadi asubuhi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22003_15', 3, NULL, NULL, 'You are free to download, copy, translate or adapt this story and use the illustrations as long as you attribute in the following way:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8713', 2, 'Teksi ya Nanu', 'assets/topic/asb/7576.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8713_3', 3, NULL, 'assets/topic/asb/7560.png', 'Hujambo! Jina langu ni Nanu. Mimi na mamangu tunasubiri teksi ili twende nyumbani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8713_4', 3, NULL, 'assets/topic/asb/7562.png', 'Hapa nimesimama kwenye mstari. Mamangu ananiimbia wimbo. "Njoo Bwana Teksi kwani huoni! Twasubiri, mimi na Nanu!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8713_5', 3, NULL, 'assets/topic/asb/7563.png', 'Mwanamke aliye karibu nami anawapeleka kuku nyumbani. Watataga mayai ili ale.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8713_6', 3, NULL, 'assets/topic/asb/7564.png', 'Wanaume hawa wanarudi kutoka kazini. Wanatabasamu. Nawasikia wakiimba, "Nanu mdogo,  usiwe na wasiwasi. Teksi yaja kwa haraka."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8713_7', 3, NULL, 'assets/topic/asb/7565.png', 'Mwanamke anapita akitembea. Yuko nadhifu sana. Viatu vyake si vizuri kweli? Vinatoa sauti, "Click, click, click. Click, click, click," anapotembea.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8713_8', 3, NULL, 'assets/topic/asb/7566.png', 'Mwanamme huyo anauza vinywaji baridi. Napenda vinywaji baridi. Ni baridi na vitamu sana. Je,  unavipenda?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8713_9', 3, NULL, 'assets/topic/asb/7571.png', 'Hebu! Kuna wanawake wamebeba vichwani chakula chao cha jioni. Je, wao si ni werevu?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8713_10', 3, NULL, 'assets/topic/asb/7568.png', 'Mwanamke huyo anasukwa nywele zake. Ataenda nje na marafiki zake leo usiku.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8713_11', 3, NULL, 'assets/topic/asb/7569.png', 'Nimechoka sasa. Mamangu ananiweka mgongoni kwake nikapumzike. Anaimba, "Tafadhali Bwana Teksi,  usichelewe. Gogo anasubiri kwenye lango kuu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8713_12', 3, NULL, 'assets/topic/asb/7570.png', 'Kuna rafiki yangu Tumi. Yeye pia anapumzika. Tutakuwa nyumbani hivi karibuni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8713_13', 3, NULL, 'assets/topic/asb/7572.png', 'Hapo juu ndege wanaruka kwenda nyumbani. Lazima wafike nyumbani kabla giza halijaingia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8713_14', 3, NULL, 'assets/topic/asb/7573.png', 'Mwanamke huyu ana vipuli maridadi. Nadhani alivinunua mjini leo. Nikiwa mkubwa, nitanunua vipuli vingi vyangu na vya mamangu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8713_15', 3, NULL, 'assets/topic/asb/7574.png', 'Mamangu anaanza kuchoka sasa. Ninamwimbia wimbo, "Tafadhali Bwana Teksi, njoo. Mimi na mamangu tunasubiri."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8713_16', 3, NULL, 'assets/topic/asb/7575.png', 'Ngoja! Ninasikia teksi! Hii hapa inakuja!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8713_17', 3, NULL, 'assets/topic/asb/7576.png', 'Hii ni teksi yetu kubwa nyekundu. Imekuja kunipeleka mimi na mamangu nyumbani. Nimefurahi sana!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8713_18', 3, NULL, 'assets/topic/asb/7577.png', 'Kwaheri!', NULL);
