INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24748', 2, 'Tembo na Kiboko', 'assets/topic/asb/27134.png', 'Ursula  Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24748_3', 3, NULL, 'assets/topic/asb/27133.png', 'Kuliishi mapacha wawili wa kiume, Tembo na Kiboko.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24748_4', 3, NULL, 'assets/topic/asb/27134.png', 'Kiboko alikuwa mvulana mwenye hasira kwa sababu ya ukoma. Upofu ulimpatia Tembo uvumilivu. Tembo alihitaji usaidizi, lakini Kiboko alimtendea ukatili.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24748_5', 3, NULL, 'assets/topic/asb/27135.png', 'Kwa sababu Kiboko alivipoteza vidole vyake, yeye pia alihitaji usaidizi. Tembo alimtendea wema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24748_6', 3, NULL, 'assets/topic/asb/27136.png', 'Asubuhi moja, mama yao aliwabagulia viazi vikuu. Kwa sababu walikuwa na njaa, wavulana hao hawakusubiri viazi hivyo vipoe.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24748_7', 3, NULL, 'assets/topic/asb/27137.png', 'Viazi vikuu vilikuwa moto sana hata vikachoma viganja vyao. Kiboko aliienua mikono yake juu na chini, kuvipoesha. Kiazi kilianguka mchangani.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24748_8', 3, NULL, 'assets/topic/asb/27138.png', '"Tafadhali, nichukulie kiazi changu," Kiboko alimwuliza Tembo.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24748_9', 3, NULL, 'assets/topic/asb/27139.png', 'Jioni, mama yao aliwatayarishia viazi vikuu pamoja na vipande vinne vya nyama. Tembo aliigusa ile nyama kwa kidole chake akahakikisha kuwa vilikuwa vipande vinne.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24748_10', 3, NULL, 'assets/topic/asb/27140.png', 'Kiboko alijaribu kuiba kipande kimoja cha nyama lakini akakiangusha. Wakati wa kugawa nyama ulipofika, Tembo aliuliza, "Si vipande vya nyama vilikuwa vinne?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24748_11', 3, NULL, 'assets/topic/asb/27141.png', 'Siku moja, walihisi njaa lakini mama yao alikuwa mbali. "Twende tucheze ili tuisahau njaa," Kiboko alipendekeza.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24748_12', 3, NULL, 'assets/topic/asb/27142.png', 'Walipokuwa njiani, Kiboko aliingia kichakani. Alilichukua jiwe nzito na kuliweka kwapani huku akikoroma. Alikuwa bado anakoroma walipokuwa wanakwenda mtoni.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24748_13', 3, NULL, 'assets/topic/asb/27143.png', 'Walipofika mtoni, Kiboko alisema, "Nitatangulia kuruka majini kisha nikusaidie kuogelea."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24748_14', 3, NULL, 'assets/topic/asb/27144.png', 'Tembo aliiuliza Fimbo kuchunguza zaidi. Aliizungusha Fimbo kila mahali hata Fimbo ikamgonga Kiboko kwa nguvu. "Ayii!" Kiboko akalia na kuruka majini.
