INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3914', 2, 'Uamsho wa furaha', 'assets/topic/asb/2462.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3914_3', 3, NULL, 'assets/topic/asb/2463.png', 'Kwa muda mrefu Mlima Olokwango alisimama akitabasamu katikati ya sehemu Tambarare ya Kwere. Kutoka kwa vibonyeo viwili vilivyokuwa pembeni mwake, kulitoka vijito vilivyokaribiana na kuunda mto.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3914_4', 3, NULL, 'assets/topic/asb/2464.png', 'Mambo yalikuwa swari hadi Matata alipokuwa mkuu wa mahali hapo. Alilazimisha njia itengenezwe kutoka juu ya mlima hadi chini yake ili aweze kupanda kwa urahis na kujifiringisha hadi chini. Mlima Olokwango alikasirika na uharibifu uliofanywa lakini alivumilia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3914_5', 3, NULL, 'assets/topic/asb/2465.png', 'Kila wikendi, watoto wa Matata na marafiki zao wangepanda mlimani kucheza. Wangekimbia kila mahali huku wakivunja vijiti na matawi, wakiwasha mioto kwa michezo yao ambayo baadaye, waliiacha ikiwaka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3914_6', 3, NULL, 'assets/topic/asb/2466.png', 'Mlima Olokwango alipoteza uvumilivu wake. Tabasamu yake nzuri ilififia. Vibonyeo viliendelea kuwa vidogo jinsi uso wake ulivyokuwa na mikunjo. Mwishowe, vijito havikuwa tena. Kingo za Mto Temu zilihuzunika. Paa walihama kwani hapakuwa tena na zile nyasi ndefu za kijani walizochezea ndani wala kulala juu yake. Mifugo, walikonda na kukondeana. Kila mtu alihisi huzuni wa Mlima Olokwango.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3914_7', 3, NULL, 'assets/topic/asb/2467.png', 'Watu wa Kwere wakachukua hatua. Wakamtoa Matata kisha wakamweka Baraka. Baraka akaamrisha kila mtu kupanda ua kuonyesha Mlima Olokwango heshima. Wakasherehekea kwa kumtolea Olokwango zawadi za msonobari, mwerezi na muhuhu. Hakuna aliyetembea juu ya mlima tena, na nyasi zikamea na kuwa ndefu tena. Polepole, tabasamu iliyokuwa usoni mwa Mlima Olokwango ilipanuka zaidi na vibonyeo vikarefuka zaidi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3914_8', 3, NULL, 'assets/topic/asb/2468.png', 'Kutoka kwenye vibonyeo, vijito vilitiririka tena. Mto Temu ulifufuliwa. Sehemu tambarare ziligeuka kijani zaidi ya hapo awali. Paa walitembea tena kwa furaha kutoka upande mmoja hadi mwingine wakila nyasi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3914_9', 3, NULL, 'assets/topic/asb/2469.png', '"Maisha marefu, Baraka! Maisha marefu, Baraka!" Watu wa Kwere waliimba. "Maisha marefu,  Olokwango," Baraka na watu wake walisema. "Heri watoto wetu na watoto wa watoto wao wakuheshimu daima."', NULL);
