INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13303', 2, 'Uamuzi', 'assets/topic/asb/10625.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13303_3', 3, NULL, 'assets/topic/asb/10609.png', 'Mtaa wetu ulikuwa na matatizo mengi. Tulisimama kwenye foleni tukisubiri kuchota maji kutoka mfereji mmoja.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13303_4', 3, NULL, 'assets/topic/asb/10616.png', 'Tulisubiri kupokea chakula cha msaada. Hatukufahamu waliokuwa wahisani wetu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13303_5', 3, NULL, 'assets/topic/asb/10614.png', 'Usalama ulizorota kila uchao. Tulilazimika kufunga nyumba zetu mapema kwa ajili ya wizi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13303_6', 3, NULL, 'assets/topic/asb/10611.png', 'Baadhi ya watoto waliacha shule. Wazazi wao hawakuweza kulipa karo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13303_7', 3, NULL, 'assets/topic/asb/10613.png', 'Wasichana wadogo walifanya kazi za nyumbani pale mtaani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13303_8', 3, NULL, 'assets/topic/asb/10615.png', 'Wavulana wadogo walirandaranda ovyo.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13303_9', 3, NULL, 'assets/topic/asb/10617.png', 'Upepo ulivuma na kuzipeperusha karatasi chafu. Ziliangikwa mitini na uani na kuyachafua mazingira.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13303_10', 3, NULL, 'assets/topic/asb/10612.png', 'Vipande vya chupa vilitupwa ovyo ovyo njiani. Watu walijeruhiwa kwa kukatwa navyo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13303_11', 3, NULL, 'assets/topic/asb/10610.png', 'Siku moja, mfereji wa maji ulikauka na mitungi yetu ikawa tupu. Tulijiuliza, "Sasa tutayapata maji wapi?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13303_12', 3, NULL, 'assets/topic/asb/10618.png', 'Babangu alitembea kutoka nyumba hadi nyumba. Aliwatangazia watu kuhudhuria mkutano wa mtaa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13303_13', 3, NULL, 'assets/topic/asb/10619.png', 'Watu walikusanyika chini ya mti ule ule ambako tulipokea chakula cha msaada. Baadhi waliuliza, "Kuna nini leo?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13303_14', 3, NULL, 'assets/topic/asb/10620.png', 'Babangu alisimama katikati yao akasema, "Lazima tuyabadilishe maisha ya mtaa wetu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13303_15', 3, NULL, 'assets/topic/asb/10621.png', 'Juma, aliyekuwa amepanda juu ya mti, alisema kwa sauti, "Naweza kusafisha uwanja wa nyumba zetu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13303_16', 3, NULL, 'assets/topic/asb/10623.png', 'Mwanamke mmoja wa makamo, alisema, "Sisi wanawake tutaungana pamoja tupande mbegu ili tupate chakula."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13303_17', 3, NULL, 'assets/topic/asb/10624.png', 'Mwanamume mwingine alisimama akasema,  "Wanaume nao watachimba kisima cha maji."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13303_18', 3, NULL, 'assets/topic/asb/10625.png', 'Sote tulisema kwa sauti moja, "Lazima tubadilishe maisha yetu."
