INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23156', 2, 'Ugali na Samaki', 'assets/topic/asb/26863.png', 'Melanie Bulouza
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23156_3', 3, NULL, 'assets/topic/asb/26864.png', 'Hapo zamani, Ugali na Samaki waliishi katika kijiji kimoja.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23156_4', 3, NULL, 'assets/topic/asb/26865.png', 'Samaki alimwambia rafiki yake Ugali waende pamoja baharini waoge.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23156_5', 3, NULL, 'assets/topic/asb/26866.png', 'Walipofika kando ya bahari, Samaki alimwambia Ugali, "Mimi naingia majini kuoga" kisha akapiga mbizi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23156_6', 3, NULL, 'assets/topic/asb/26867.png', 'Baada ya kuogelea kwa muda mfupi, Samaki alimwambia Ugali, "Sasa ni zamu yako kuogelea."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23156_7', 3, NULL, 'assets/topic/asb/26868.png', 'Samaki alimbembeleza Ugali akisema, "Njoo ujaribu. Hutayeyuka na ukizama, nitakuokoa." Ugali alikubali.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23156_8', 3, NULL, 'assets/topic/asb/26869.png', 'Mtu mmoja alimsikia Samaik akilalamika kwamba hangeweza kumwoka Ugali. Mtu huyo akamwuliza, "Kuna shida gani?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23156_9', 3, NULL, 'assets/topic/asb/26870.png', 'Ugali alipotoka majini, alisema, "Samaki ni rafiki mbaya. Amenishawishi niogelee nami sijui kuogelea."
