INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16210', 2, 'Ujasiri wa Nangila', 'assets/topic/asb/15103.png', 'Ursula Nafula', 'FF9DEDE3');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16210_3', 3, NULL, 'assets/topic/asb/15103.png', 'Wekesa alikuwa na donda mguuni. Yeye na mkewe waliishi kijijini na wanao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16210_4', 3, NULL, 'assets/topic/asb/15104.png', 'Binti yao, Nangila, na kaka zake watatu walifanya kazi zao vizuri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16210_5', 3, NULL, 'assets/topic/asb/15105.png', 'Nangila aliwavutia wengi. Mwanamme aliyependa kumwoa, alitakiwa kutimiza sharti kali lililowekwa na babake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16210_6', 3, NULL, 'assets/topic/asb/15106.png', 'Sharti lilikuwa kuileta miti-shamba kutoka ziwa lililokuwa karibu. Dawa hiyo ingeponya donda alilokuwa nalo bababke Nangila. Ziwa hilo lilijaa mizimu hatari.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16210_7', 3, NULL, 'assets/topic/asb/15107.png', 'Baadhi ya wanaume waliojaribu kuipata ile miti-shamba, walirudi mikono mitupu. Wengine kamwe hawakurejea kutoka ziwani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16210_8', 3, NULL, 'assets/topic/asb/15109.png', 'Nangila alikuwa na hofu kaka zake pia waliposhindwa kwenda ziwani. Aliamua kwenda mwenyewe. Mamake alimwambia, "Mwanangu, wanaume wameshindwa, utafaulu?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16210_9', 3, NULL, 'assets/topic/asb/15110.png', 'Nangila alimsaidia bi kizee mmoja kubeba kuni. Bi kizee alimshukuru, akamwambia, "Nitakueleza jinsi utakavyofika kwenye ziwa la mizimu na namna utakavyofanya."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16210_10', 3, NULL, 'assets/topic/asb/15111.png', 'Nangila alipofika kwenye ufuo wa ziwa, aliiona familia ya mizimu ikinywa na kusherehekea. Alisubiri na kutazama. Moja baada ya nyingine, mizimu ilipatwa na usingizi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16210_11', 3, NULL, 'assets/topic/asb/15112.png', 'Mizimu yote ilipolala,  Nangila alipiga mbizi ziwani. Aliipata miti-shamba na kuiweka mkobani. Alipoogelea kurudi ufuoni,  mawimbi makubwa yalimzunguka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16210_12', 3, NULL, 'assets/topic/asb/15113.png', 'Alifika ufuoni mizimu ikiwa imeamka. Aliimba wimbo aliofunzwa na bi kizee:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16210_13', 3, NULL, 'assets/topic/asb/15114.png', 'Sauti ya Nangila ilikuwa tamu sana hata mizimu ikamtaka aendelee kuimba. Mizimu ilipolala tena, Nangila alikimbia haraka kwenda kijijini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16210_14', 3, NULL, 'assets/topic/asb/15115.png', 'Baada ya kutibu donda la babake, babake aliweza kusimama tena. Kijiji kizima kilisherehekea na kumpongeza Nangila.', NULL);
