INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15614', 2, 'Uji', 'assets/topic/asb/13256.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15614_3', 3, NULL, 'assets/topic/asb/7923.png', 'Zama anajitayarisha kwenda shule.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15614_4', 3, NULL, 'assets/topic/asb/7924.png', 'Baba anapika uji wa kiamsha kinywa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15614_5', 3, NULL, 'assets/topic/asb/7925.png', '"Mama hutayarisha uji mzuri zaidi." Zama anasema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15614_6', 3, NULL, 'assets/topic/asb/13254.png', 'Uji unampa Zama nguvu za kucheza shuleni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15614_7', 3, NULL, 'assets/topic/asb/13255.png', 'Uji uko tayari.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15614_8', 3, NULL, 'assets/topic/asb/13256.png', 'Baba anaongeza uji maziwa kwa upendo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15614_9', 3, NULL, 'assets/topic/asb/13257.png', '"Baba,  ninafikiri uji huu unahitaji sukari zaidi," Zama anasema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15614_10', 3, NULL, 'assets/topic/asb/13259.png', '"Uji wa mama una ladha kuliko wa baba," Zama anawaza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15614_11', 3, NULL, 'assets/topic/asb/13258.png', '"Baba,  tafadhali naweza kupata sukari zaidi?" Zama anauliza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15614_12', 3, NULL, 'assets/topic/asb/13260.png', 'Baba anamwongezea Zama ''sukari.''.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15614_13', 3, NULL, 'assets/topic/asb/13261.png', '"Wu! Baba! Umeweka chumvi badala ya sukari!" Zama analalamika.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15614_14', 3, NULL, 'assets/topic/asb/13262.png', '"Baba,  je,  mama atarudi lini nyumbani?" Zama anauliza.', NULL);
