INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23033_6', 3, NULL, 'assets/topic/asb/10421.png', 'Metobo ana tatizo la macho.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23033_7', 3, NULL, 'assets/topic/asb/10422.png', 'Huyu ni jirani yangu Moraa. Moraa ni mkulima.', NULL);
