INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18344_3', 3, NULL, 'assets/topic/asb/10609.png', 'Kijiji chetu kilikuwa na shida nyingi. Tulikuwa na mfereji mmoja.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18344_4', 3, NULL, 'assets/topic/asb/10616.png', 'Tulitegemea chakula cha msaada.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18344_5', 3, NULL, 'assets/topic/asb/10614.png', 'Tuliogopa kuvamiwa na wezi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18344_6', 3, NULL, 'assets/topic/asb/10611.png', 'Wazazi wengine hawakuweza kulipa karo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18344_7', 3, NULL, 'assets/topic/asb/10613.png', 'Wasichana wadogo waliacha shule.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18344_8', 3, NULL, 'assets/topic/asb/10615.png', 'Wavulana wadogo walirandaranda kijijini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18344_9', 3, NULL, 'assets/topic/asb/10617.png', 'Uchafu ulikuwa kila mahali.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18344_10', 3, NULL, 'assets/topic/asb/10612.png', 'Watu walikatwa na vipande vya chupa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18344_11', 3, NULL, 'assets/topic/asb/10610.png', 'Siku moja,  mfereji ulikosa maji.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18344_12', 3, NULL, 'assets/topic/asb/10618.png', 'Babangu aliwaita watu waende mkutano.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18344_13', 3, NULL, 'assets/topic/asb/10619.png', 'Watu walikusanyika wakajadiliana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18344_14', 3, NULL, 'assets/topic/asb/10620.png', 'Babangu alisema,  "Lazima tuyabadilishe maisha yetu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18344_15', 3, NULL, 'assets/topic/asb/10621.png', 'Mimi nilisema,  "Nitasafisha uwanja wa nyumba yetu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18344_16', 3, NULL, 'assets/topic/asb/10623.png', 'Mamake Dama alisema,  "Wanawake wataungana wapande chakula."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18344_17', 3, NULL, 'assets/topic/asb/10624.png', 'Babake Mumo alisema,  "Wanaume watachimba kisima cha maji."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18344_18', 3, NULL, 'assets/topic/asb/10625.png', 'Kwa sauti moja tulisema,  "Tuyabadilishe maisha yetu."', NULL);
