INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18345', 2, 'Upepo mkali', 'assets/topic/asb/14101.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18345_3', 3, NULL, 'assets/topic/asb/14102.png', 'Nilikuwa nikicheza na tiara yangu. Upepo mkali ulianza kuvuma.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18345_4', 3, NULL, 'assets/topic/asb/14103.png', 'Upepo huo uliibandua tiara yangu. Ilirushwa mbali.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18345_5', 3, NULL, 'assets/topic/asb/14104.png', 'Macho yangu yalijaa mchanga.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18345_6', 3, NULL, 'assets/topic/asb/14105.png', 'Upepo huo uligeuka ukawa dhoruba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18345_7', 3, NULL, 'assets/topic/asb/14106.png', 'Niliogopa!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18345_8', 3, NULL, 'assets/topic/asb/14107.png', 'Nilijiuliza, "Tiara  yangu nzuri iko wapi?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18345_9', 3, NULL, 'assets/topic/asb/14108.png', 'Labda,  ilikuwa angani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18345_10', 3, NULL, 'assets/topic/asb/14109.png', 'Dhoruba hiyo ilinizungusha. Iliniinua juu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18345_11', 3, NULL, 'assets/topic/asb/14110.png', 'Iliniangusha chini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18345_12', 3, NULL, 'assets/topic/asb/14111.png', 'Niliposimama, sikuiona tiara yangu. Sikuusikia upepo ukivuma tena.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18345_13', 3, NULL, 'assets/topic/asb/14112.png', 'Nilianza kwenda nyumbani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18345_14', 3, NULL, 'assets/topic/asb/14101.png', 'Je, unafahamu kuwa upepo mkali ni hatari?', NULL);
