INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23751', 2, 'Urafiki aina gani?', 'assets/topic/asb/18784.png', 'HANNINGTON  OWERE
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23751_3', 3, NULL, 'assets/topic/asb/18783.png', 'Hapo zamani za kale, Chura na Nyoka walikuwa marafiki wazuri.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23751_4', 3, NULL, 'assets/topic/asb/18784.png', 'Siku moja, Chura alimwambia Nyoka, "Rafiki yangu, ningependa kumtembelea mamangu wiki ijayo. Je, ungetaka kwenda nami?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23751_5', 3, NULL, 'assets/topic/asb/25000.png', '"Ndiyo, ningependa kwenda nawe," Nyoka akajibu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23751_6', 3, NULL, 'assets/topic/asb/25001.png', 'Walimwomba Tumbili awasaidie kwa sababu aliwajibika na alikuwa mwenye roho nzuri.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23751_7', 3, NULL, 'assets/topic/asb/25002.png', 'Siku zilizofuata, Chura na Nyoka walijishughulisha kupanga safari yao.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23751_8', 3, NULL, 'assets/topic/asb/25003.png', 'Siku ya kusafiri ilipofika, marafiki hao waliondoka asubuhi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23751_9', 3, NULL, 'assets/topic/asb/25004.png', 'Nyoka alikuwa na hofu akaanza kutetemeka.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23751_10', 3, NULL, 'assets/topic/asb/25005.png', 'Kindi walipowazingira, Chura alipiga kelele sana.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23751_11', 3, NULL, 'assets/topic/asb/25006.png', 'Chura na Nyoka walipofika kwa mamake Chura, walikuwa wamechoka na kuhisi njaa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23751_12', 3, NULL, 'assets/topic/asb/25007.png', 'Wakati chakula kilikuwa tayari, Chura na Nyoka walinawa tayari kula.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23751_13', 3, NULL, 'assets/topic/asb/25008.png', 'Kabla ya kuanza kula, Chura alimwambia Nyoka, "Huwezi kula ukiwa umelala. Hiyo ni tabia mbaya. Tafadhali keti wima ili mamangu asikasirike."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23751_14', 3, NULL, 'assets/topic/asb/25009.png', 'Nyoka alijaribu sana kuketi kama vile Chura, lakini alishindwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23751_15', 3, NULL, 'assets/topic/asb/25010.png', 'Wakati huo huo Chura na mamake waliendelea kula chakula.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23751_16', 3, NULL, 'assets/topic/asb/25011.png', 'Nyoka alimlalamikia Chura, "Mimi siwezi kuketi wima kama ninyi mnavyoketi."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23751_17', 3, NULL, 'assets/topic/asb/25012.png', 'Nyoka alikasirika sana hata akaamua kurudi nyumbani kwake.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23751_18', 3, NULL, 'assets/topic/asb/25013.png', 'Alipokuwa njiani, alipanga namna angemwadhibu Chura!', NULL);
