INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17937', 2, 'Urithi kutoka kwa baba', 'assets/topic/asb/17228.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17937_3', 3, NULL, 'assets/topic/asb/17218.png', 'Eba na mkewe Lelisa walifanya kazi zao kwa bidii sana. Walikuwa na watoto wawili wa kiume.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17937_4', 3, NULL, 'assets/topic/asb/17219.png', 'Walitimiza mahitaji yao kutokana na juhudi zao. Eba alitaka wanawe wastarehe. Hawakufanya kazi zozote.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17937_5', 3, NULL, 'assets/topic/asb/17220.png', 'Baada ya muda, Lelisa alifariki.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17937_6', 3, NULL, 'assets/topic/asb/17221.png', 'Hata baada ya kifo cha mkewe, Eba hakutaka wanawe wamsaidie. Alizifanya kazi zote za shambani na za nyumbani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17937_7', 3, NULL, 'assets/topic/asb/17222.png', 'Muda ulipopita, Eba alizeeka na kudhoofika. Alihofia maisha ya wanawe. Alijiuliza, "Wanangu wataishi namna gani nitakapofariki?" Alijua kuwa hawangeweza kujitegemea.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17937_8', 3, NULL, 'assets/topic/asb/17223.png', 'Eba aliwaita wanawe akawaambia, "Urithi mtakaopata kutoka kwangu ni dhahabu nyingi iliyofichwa shambani. Ni juu yenu kuyalima mashamba ili muipate dhahabu hiyo."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17937_9', 3, NULL, 'assets/topic/asb/17666.png', 'Eba pia aliwaambia majirani, "Nadhani niko karibu kufariki. Wanangu hawajui kujitegemea. Nawasihi mwasaidie hadi watakapoweza kujikimu wenyewe."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17937_10', 3, NULL, 'assets/topic/asb/17669.png', 'Baada ya Eba kufariki, majirani waliwapa wanawe chakula kwa muda.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17937_11', 3, NULL, 'assets/topic/asb/17226.png', 'Ndugu hao hawakutaka kuporwa dhahabu iliyofichwa na baba yao. Waliamua kuyalima mashamba wenyewe ili waipate dhahabu hiyo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17937_12', 3, NULL, 'assets/topic/asb/17227.png', 'Waliyalima mashamba moja baada ya jingine. Ila hawakupata dhahabu yoyote.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17937_13', 3, NULL, 'assets/topic/asb/17228.png', 'Mmoja alimwuliza mwingine, "Je, unadhani baba alitudanganya?" Yule akajibu, "La, ninadhani baba alikuwa na maana kuwa ukulima ni dhahabu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17937_14', 3, NULL, 'assets/topic/asb/17229.png', 'Kwa hivyo, kama wakulima wengine, wana hao waliyalima mashamba yao. Walipanda mimea wakawa matajiri sana.', NULL);
