INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816', 2, 'Ushauri mbaya', 'assets/topic/asb/17057.png', 'African Storybook and Dorcas Wepukhulu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_3', 3, NULL, 'assets/topic/asb/17050.png', 'Kombe na Juma walikuwa maskini pia jirani.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_4', 3, NULL, 'assets/topic/asb/17052.png', 'Walitamani kuwa na punda.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_5', 3, NULL, 'assets/topic/asb/17053.png', 'Kombe akasema, "Hebu tununue punda pamoja."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_6', 3, NULL, 'assets/topic/asb/17054.png', 'Babake Kombe alipofariki, Kombe alitajirika.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_7', 3, NULL, 'assets/topic/asb/17055.png', 'Kombe alitaka nyama ya punda amlishe mbwa wake.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_8', 3, NULL, 'assets/topic/asb/17057.png', 'Hakimu aliwaambia, "Kila mmoja wenu anaweza kuchukua nusu ya punda."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_9', 3, NULL, 'assets/topic/asb/17058.png', 'Kombe alimchinja punda.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_10', 3, NULL, 'assets/topic/asb/17060.png', 'Kombe alitaka kuteketeza chumba chake ili ajenga kipya.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_11', 3, NULL, 'assets/topic/asb/17061.png', 'Juma alisema, "Changu pia kitachomeka."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_12', 3, NULL, 'assets/topic/asb/17062.png', 'Chumba cha Juma kiliungua.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_13', 3, NULL, 'assets/topic/asb/17063.png', 'Juma hakuwa na punda wala chumba.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_14', 3, NULL, 'assets/topic/asb/17064.png', 'Juma alifanya kazi kwa bidii.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_15', 3, NULL, 'assets/topic/asb/17066.png', 'Wavulana wa Kombe walikula mbaazi karibu wazimalize zote.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_16', 3, NULL, 'assets/topic/asb/17068.png', 'Walimwambia Juma, "Nenda umwulize baba akulipe mbaazi zako."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_17', 3, NULL, 'assets/topic/asb/17069.png', 'Juma alisema, "Sitaki pesa zenu. Ninataka mbaazi zangu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_18', 3, NULL, 'assets/topic/asb/17070.png', 'Hakimu alimwambia Juma, "Wakate wavulana halafu uchukue mbaazi zako."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_19', 3, NULL, 'assets/topic/asb/17071.png', 'Juma alikataa kusikiliza alivyoambiwa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_20', 3, NULL, 'assets/topic/asb/17073.png', 'Wazee walisema, "Ulimuua punda. Ulichoma chumba chake."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_21', 3, NULL, 'assets/topic/asb/17074.png', 'Walimwambia Juma, "Kuwaua wavulana wa Kombe ni vibaya pia."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21816_22', 3, NULL, 'assets/topic/asb/17075.png', 'Kombe alimpatia Juma nusu ya mali yake.
